                    CFG_GLPI: true,
                    tinyMCE: true,
                    __: true,
                    _n: true,
                    _x: true,
                    _nx: true
                }
            },
            sourceType: "script"
        },
        rules: {
            "eol-last": [
                "error",
                "always"
            ],
