                "error",
                {
                    "vars": "local"
                }
            ],
            "quotes": [
                "off",
                "single"
            ],
            "semi": [
                "error",
                "always"
            ],
            "no-var": "error",
            "prefer-arrow-callback": "error",
            "no-eval": "error",
            "no-implied-eval": "error",
            "prefer-const": "error",
            "prefer-spread": "error",
            "prefer-template": "error",
        }
    },
    {
        // Modules
        files: ["js/modules/**", "eslint.config.mjs"],
        languageOptions: {
            sourceType: "module"
        }
    },
    ...vue.configs["flat/essential"].map(config => ({
        ...config,
        files: ["js/src/**", "tests/js/**"]
    })),
    {
