    },
    {
        // Config files
        "files": ["eslint.config.mjs", ".stylelintrc.js", ".webpack.config.js", ".vue.webpack.config.js", "tests/cypress.config.js"],
        "languageOptions": {
            "globals": {...globals.node}
        },
        "rules": {
            "prefer-template": "off",
        }
    }
];

