{
    "name": "statamic/cms",
    "description": "The Statamic CMS Core Package",
    "keywords": [
        "statamic",
        "cms",
        "flat file",
        "laravel"
    ],
    "license": "proprietary",
    "require": {
        "ext-json": "*",
        "ajthinking/archetype": "^1.0.3 || ^2.0",
        "composer/semver": "^3.4",
        "guzzlehttp/guzzle": "^6.3 || ^7.0",
