{
    "preset": "laravel",
    "rules": {
        "class_attributes_separation": {
            "elements": {
                "method": "one"
            }
        },
        "psr_autoloading": true
    }
}

module.exports = {
    plugins: {
        'postcss-import': {},
        'tailwindcss/nesting': {},
        tailwindcss: {},
        autoprefixer: {},
    },
};

module.exports = {
    plugins: [
      {
        name: 'preset-default',
      },
      'sortAttrs',
      'removeDimensions',
    ],
  };

import { defineConfig } from 'vite';

export default defineConfig({
    build: {
        outDir: 'resources/dist-frontend/js',
        lib: {
            entry: 'resources/js/frontend/helpers.js',
            name: 'statamic',
            fileName: () => 'helpers.js',
            formats: ['umd'],
        }
    }
});

const fs = require('fs').promises
const { compileTemplate } = require('@vue/compiler-sfc')
const { optimize: optimizeSvg } = require('svgo')

module.exports = function svgLoader (options = {}) {
  const { svgoConfig, svgo, defaultImport } = options

  const svgRegex = /\.svg(\?(raw|component|skipsvgo))?$/

  return {
    name: 'svg-loader',
    enforce: 'pre',

    async load (id) {
      if (!id.match(svgRegex)) {
        return
      }

      const [path, query] = id.split('?', 2)

      const importType = query || defaultImport

      if (importType === 'url') {
        return // Use default svg loader
      }

      let svg

      try {
        svg = await fs.readFile(path, 'utf-8')
      } catch (ex) {
        console.warn('\n', `${id} couldn't be loaded by vite-svg-loader, fallback to default loader`)
        return
      }

      if (importType === 'raw') {
        return `export default ${JSON.stringify(svg)}`
      }

      if (svgo !== false && query !== 'skipsvgo') {
        svg = optimizeSvg(svg, {
          ...svgoConfig,
          path
        }).data
      }

      const { code } = compileTemplate({
        id: JSON.stringify(id),
        source: svg,
        filename: path,
        transformAssetUrls: false
      })

      return `${code}\nexport default { render: render }`
    }
  }
}

module.exports.default = module.exports

import { defineConfig, loadEnv } from 'vite';
import laravel from 'laravel-vite-plugin';
import vue from '@vitejs/plugin-vue2';
import inject from '@rollup/plugin-inject';
import svgLoader from './vite-svg-loader';

export default defineConfig(({ mode }) => {
    const env = loadEnv(mode, process.cwd(), '');

    return {
        base: './',
        plugins: [
            laravel({
                valetTls: env.VALET_TLS,
                input: [
                    'resources/css/tailwind.css',
                    'resources/js/app.js'
                ],
                refresh: true,
                publicDirectory: 'resources/dist',
                hotFile: 'resources/dist/hot',
            }),
            vue(),
            svgLoader(),
            inject({
                Vue: 'vue',
                _: 'underscore',
                include: 'resources/js/**'
            })
        ],
        resolve: {
            alias: {
                vue: 'vue/dist/vue.esm.js',
            }
        }
    }
});
