{
  "presets": [
    ["@babel/preset-env", {
      "modules": false,
      "targets": [
        ">0.25%",
        "not ie 11",
        "not op_mini all"
      ]
    }]
  ],
  "plugins": [
    "@babel/plugin-proposal-class-properties"
  ],
  "env": {
    "test": {
      "presets": ["@babel/env"]
    }
  }
}

module.exports = {
  root: true,
  env: {
    node: true
  },
  extends: ['plugin:vue/essential', '@vue/standard', 'prettier'],
  rules: {
    'no-unmodified-loop-condition': process.env.NODE_ENV === 'production' ? 'error' : 'warn',
    'no-unused-vars': process.env.NODE_ENV === 'production' ? 'error' : 'warn',
    'no-unreachable': process.env.NODE_ENV === 'production' ? 'error' : 'warn',
    // indent
    indent: ['error', 2, { SwitchCase: 1 }],
    'vue/script-indent': [
      process.env.NODE_ENV === 'production' ? 'error' : 'warn',
      2,
      {
        baseIndent: 1,
        switchCase: 1
      }
    ],
    'no-useless-escape': 0,
    // allow paren-less arrow functions
    'arrow-parens': 0,
    // allow async-await
    'generator-star-spacing': 0,
    // allow hasOwnProperty
    'no-prototype-builtins': 0,
    // allow debugger during development
    'no-debugger': process.env.NODE_ENV === 'production' ? 2 : 0,
    'no-console':
      process.env.NODE_ENV === 'production'
        ? ['error', { allow: ['error'] }]
        : 'warn'
  },
  overrides: [
    {
      files: ['*.vue'],
      rules: {
        indent: 'off',
        'vue/script-indent': [
          process.env.NODE_ENV === 'production' ? 'error' : 'warn',
          2,
          {
            baseIndent: 1,
            switchCase: 1
          }
        ]
      }
    }
  ],
  parserOptions: {
    parser: '@babel/eslint-parser'
  }
}

module.exports = {
  presets: [
    ['@vue/cli-plugin-babel/preset', {
      useBuiltIns: 'entry'
      // polyfills: [
      //   'es6.promise',
      //   'es6.symbol',
      //   'es6.array.find-index',
      //   'es6.array.iterator',
      //   'es6.function.name',
      //   'es6.number.constructor',
      //   'es6.object.assign',
      //   'es6.object.keys',
      //   'es6.regexp.constructor',
      //   'es6.regexp.match',
      //   'es6.regexp.replace',
      //   'es6.regexp.to-string',
      //   'es6.regexp.split',
      //   'es6.string.anchor',
      //   'es6.string.includes',
      //   'es6.string.starts-with',
      //   'es7.array.includes',
      //   'es7.object.get-own-property-descriptors'
      // ]
    }]
  ]
}

{
  "name": "area17/twill",
  "type": "library",
  "license": "Apache-2.0",
  "description": "Twill is an open source CMS toolkit for Laravel that helps developers rapidly create a custom admin console that is intuitive, powerful and flexible.",
  "keywords": [
    "A17",
    "twill",
    "laravel",
    "cms"
  ],
  "homepage": "https://twillcms.com",
  "authors": [
    {
      "name": "AREA 17",
      "email": "hello@twill.io",
      "homepage": "https://area17.com"
    }
  ],
  "support": {
    "issues": "https://github.com/area17/twill/issues/",
    "docs": "https://twillcms.com/docs/",
    "chat": "https://discord.gg/cnWk7EFv8R"
  },
  "require": {
    "php": "^8.0",
    "ext-json": "*",
    "ext-pdo": "*",
    "astrotomic/laravel-translatable": "^v11.12",
    "cartalyst/tags": "^12.0 || ^13.0 || ^14.0",
    "doctrine/dbal": "^3.0 || ^4.0",
    "guzzlehttp/guzzle": "^7.0",
    "imgix/imgix-php": "^3.0",
    "kalnoy/nestedset": "^6.0",
    "laravel/framework": "^9.0 || ^10.0 || ^11.0",
    "laravel/socialite": "^5.3",
    "laravel/ui": "^4.0",
    "league/flysystem-aws-s3-v3": "^3.0",
    "league/glide-symfony": "^1.0 || ^2.0",
    "matthewbdaly/laravel-azure-storage": "^2.0",
    "myclabs/php-enum": "^1.5",
    "pragmarx/google2fa-qrcode": "^2.0",
    "spatie/laravel-activitylog": "^4.0",
    "spatie/laravel-analytics": "^4.0 || ^5.0",
    "spatie/once": "^3.0"
  },
  "require-dev": {
    "area17/phptorch": "dev-main",
    "chillerlan/php-qrcode": "~4.0",
    "friendsofphp/php-cs-fixer": "^3.0",
    "nette/php-generator": "^4.0.3",
    "nunomaduro/collision": "^6.0 || ^7.0 || ^8.0",
    "nunomaduro/larastan": "^2.0",
    "orchestra/testbench": "^7.8 || ^8.0 || ^9.0",
    "orchestra/testbench-dusk": "^7.8 || ^8.0 || ^9.0",
    "phpunit/php-invoker": "^3.1 || ^4.0",
    "phpunit/phpunit": "~9.0 || ~10.0",
    "rector/rector": "^0.19.2 || ^1.0 ",
    "spatie/invade": "^1.1",
    "squizlabs/php_codesniffer": "*"
  },
  "autoload": {
    "psr-4": {
      "A17\\Twill\\": "src/"
    }
  },
  "autoload-dev": {
    "psr-4": {
      "A17\\Twill\\Tests\\Unit\\": "tests/unit",
      "A17\\Twill\\Tests\\Integration\\": "tests/integration",
      "A17\\Twill\\Tests\\Browser\\": "tests/Browser",
      "A17\\Docs\\": "docs/generator",
      "App\\": "vendor/orchestra/testbench-dusk/laravel/app"
    }
  },
  "scripts": {
    "test:phpunit": "vendor/bin/phpunit",
    "test:analyse": "php -d memory_limit=-1 vendor/bin/phpstan analyse",
    "test:syntax": "vendor/bin/php-cs-fixer --dry-run fix src",
    "test": [
      "@test:syntax",
      "@test:phpunit"
    ],
    "post-autoload-dump": [
      "@php ./vendor/bin/testbench package:discover --ansi"
    ]
  },
  "extra": {
    "laravel": {
      "providers": [
        "A17\\Twill\\TwillServiceProvider"
      ],
      "aliases": {
        "TwillCapsules": "A17\\Twill\\Facades\\TwillCapsules",
        "TwillRoutes": "A17\\Twill\\Facades\\TwillRoutes",
        "TwillBlocks": "A17\\Twill\\Facades\\TwillBlocks",
        "TwillUtil": "A17\\Twill\\Facades\\TwillUtil",
        "TwillAppSettings": "A17\\Twill\\Facades\\TwillAppSettings",
        "TwillNavigation": "A17\\Twill\\Facades\\TwillNavigation",
        "TwillPermissions": "A17\\Twill\\Facades\\TwillPermissions"
      }
    }
  },
  "minimum-stability": "dev",
  "prefer-stable": true,
  "config": {
    "sort-packages": true,
    "allow-plugins": {
      "cweagans/composer-patches": true
    }
  }
}

{
  "$schema": "https://laravel-ide.com/schema/laravel-ide-v2.json",
  "blade": {
    "directives":
    [
      {
        "name": "formField",
        "prefix": "<?php [",
        "suffix": "]; ?>"
      },
      {
        "name": "twillBlockTitle",
        "prefix": "<?php [",
        "suffix": "]; ?>"
      },
      {
        "name": "twillBlockIcon",
        "prefix": "<?php [",
        "suffix": "]; ?>"
      },
      {
        "name": "twillBlockGroup",
        "prefix": "<?php [",
        "suffix": "]; ?>"
      },
      {
        "name": "formConnectedFields",
        "prefix": "<?php [",
        "suffix": "]; ?>"
      },
      {
        "name": "endformConnectedFields"
      }
    ]
  },
  "completions": [
    {
      "complete": "routeName",
      "condition": [
        {
          "methodNames": [
            "forRoute"
          ],
          "classFqn": [
            "\\A17\\Twill\\View\\Components\\Navigation\\NavigationLink"
          ],
          "parameters": [
            1
          ]
        }
      ]
    }
  ]
}

