@charset "utf-8";
/*
Theme Name: Sakura
Theme URI: https://github.com/mashirozx/Sakura/
Author: Mashiro, Spirit, Louie, Fuzzz
Author URI: http://2heng.xin
Description: A wonderful branch of theme Akina
Version: 3.4.0
License: GNU General Public License v2 or later
License URI: http://www.gnu.org/licenses/gpl-2.0.html
Text Domain: sakura
Tags: 樱花庄的白猫
*/

#content {
    animation: main 1s
}

.headertop,
.pattern-center {
    animation: header 1s
}

.site-branding {
    animation: sitetop 1s
}

.header-user-avatar,
.searchbox,
.site-top .lower {
    animation: searchbox 1s
}

.comments,
.notice,
.post-list,
.post-list-thumb,
.top-feature {
    animation: main .6s
}

@keyframes header {
    0% {
        opacity: 0;
        transform: translateY(-50px)
    }
    100% {
        opacity: 1;
        transform: translateY(0)
    }
}

@keyframes main {
    0% {
        opacity: 0;
        transform: translateY(50px)
    }
    100% {
        opacity: 1;
        transform: translateY(0)
    }
}

@keyframes sitetop {
    0% {
        opacity: 0;
        transform: translateX(-30px)
    }
    100% {
        opacity: 1;
        transform: translateX(0)
    }
}

@keyframes searchbox {
    0% {
        opacity: 0;
        transform: translateX(30px)
    }
    100% {
        opacity: 1;
        transform: translateX(0)
    }
}

html {
    font-family: sans-serif;
    -webkit-text-size-adjust: 100%;
    -ms-text-size-adjust: 100%;
    overflow-x: hidden
}

body {
    margin: 0;
    font: 300 17px;
    text-shadow: 0 0 1px rgba(0, 0, 0, .1)
}

input:-webkit-autofill {
    -webkit-box-shadow: 0 0 0 1000px #fff inset
}

::selection {
    background: #444;
    color: #fff
}

::-moz-selection {
    background: #444;
    color: #fff
}

article,
aside,
details,
figcaption,
figure,
footer,
header,
main,
menu,
nav,
section,
summary {
    display: block
}

audio,
canvas,
progress,
video {
    display: inline-block;
    vertical-align: baseline
}

audio:not([controls]) {
    display: none;
    height: 0
}

[hidden],
template {
    display: none
}

a {
    background-color: transparent;
    text-decoration: none;
    cursor: url(https://cdn.jsdelivr.net/gh/moezx/cdn@3.1.9/img/Sakura/cursor/ayuda.cur), auto
}

a:active,
a:hover {
    outline: 0
}

a:active {
    cursor: url(https://cdn.jsdelivr.net/gh/moezx/cdn@3.1.9/img/Sakura/cursor/work.cur), alias
}

abbr[title] {
    border-bottom: 1px dotted
}

b,
strong {
    font-weight: 700
}

dfn {
    font-style: italic
}

h1 {
    font-size: 2em;
    margin: .67em 0
}

mark {
    background: #ff0;
    color: #000
}

small {
    font-size: 80%
}

sub,
sup {
    font-size: 75%;
    line-height: 0;
    position: relative;
    vertical-align: baseline
}

sup {
    top: -.5em
}

sub {
    bottom: -.25em
}

img {
    border: 0
}

svg:not(:root) {
    overflow: hidden
}

figure {
    margin: 1em 40px
}

hr {
    box-sizing: content-box;
    height: 0
}

pre {
    overflow: auto
}

code,
kbd,
pre,
samp {
    font-family: monospace, monospace;
    font-size: 1em
}

button,
input,
optgroup,
select,
textarea {
    color: inherit;
    font: inherit;
    margin: 0
}

button {
    overflow: visible
}

button,
select {
    text-transform: none
}

button,
html input[type=button],
input[type=reset],
input[type=submit] {
    -webkit-appearance: button;
    cursor: pointer
}

button[disabled],
html input[disabled] {
    cursor: default
}

button::-moz-focus-inner,
input::-moz-focus-inner {
    border: 0;
    padding: 0
}

input {
    line-height: normal
}

input[type=checkbox],
input[type=radio] {
    box-sizing: border-box;
    padding: 0
}

input[type=number]::-webkit-inner-spin-button,
input[type=number]::-webkit-outer-spin-button {
    height: auto
}

input[type=search]::-webkit-search-cancel-button,
input[type=search]::-webkit-search-decoration {
    -webkit-appearance: none
}

fieldset {
    border: 1px solid silver;
    margin: 0 2px;
    padding: .35em .625em .75em
}

legend {
    border: 0;
    padding: 0
}

textarea {
    overflow: auto
}

optgroup {
    font-weight: 700
}

table {
    border-collapse: collapse;
    border-spacing: 0
}

td,
th {
    padding: 0
}

body,
button,
input,
select,
textarea {
    color: #404040;
    font-size: 15px;
    line-height: 1.5
}

h1,
h2,
h3,
h4,
h5,
h6 {
    clear: both
}

p {
    margin-bottom: 1.5em;
    cursor: url(https://cdn.jsdelivr.net/gh/moezx/cdn@3.1.9/img/Sakura/cursor/texto.cur), auto
}

cite,
dfn,
em,
i {
    font-style: italic
}

blockquote {
    margin: 0;
    padding: 30px 60px;
    position: relative
}

blockquote:before {
    content: "\f10d" !important;
    font-size: 3rem;
    position: absolute;
    top: -25px;
    left: 12px;
    color: orange;
    font-family: FontAwesome
}

blockquote:after {
    content: '\f10e' !important;
    font-size: 3rem;
    position: absolute;
    bottom: -25px;
    right: -5px;
    color: orange;
    font-family: FontAwesome
}

blockquote p {
    margin: 5px 0
}

address {
    margin: 0 0 1.5em
}

pre {
    background: rgba(254, 250, 199, .5);
    border-radius: 10px;
    font: 15px/22px "Microsoft YaHei", Arial, Sans-Serif;
    line-height: 1.6;
    margin-bottom: 1.6em;
    max-width: 100%;
    overflow: auto;
    text-shadow: none;
    color: #000
}

code,
kbd,
tt,
var {
    font-size: 15px;
    font-size: .9375rem
}

abbr,
acronym {
    border-bottom: 1px dotted #666;
    cursor: help
}

ins,
mark {
    background: #fff9c0;
    text-decoration: none
}

big {
    font-size: 125%
}

html {
    box-sizing: border-box
}

* {
    box-sizing: border-box
}

:after,
:before {
    box-sizing: inherit
}

body {
    background: #fff;
    background-attachment: fixed;
    cursor: url(https://cdn.jsdelivr.net/gh/moezx/cdn@3.1.9/img/Sakura/cursor/normal.cur), auto
}

blockquote:after,
blockquote:before,
q:after,
q:before {
    content: ""
}

blockquote,
q {
    quotes: "" ""
}

hr {
    background-color: #ccc;
    border: 0;
    height: 1px;
    margin-bottom: 1.5em
}

ol,
ul {
    margin: 0 0 1.5em 3em
}

ul {
    list-style: disc
}

ol {
    list-style: decimal
}

li>ol,
li>ul {
    margin-bottom: 0;
    margin-left: 1.5em
}

dt {
    font-weight: 700
}

dd {
    margin: 0 1.5em 1.5em
}

img {
    height: auto;
    max-width: 100%
}

table {
    margin: 0 0 1.5em;
    width: 100%
}

button,
input[type=button],
input[type=reset],
input[type=submit] {
    border: 1px solid;
    border-color: #ccc #ccc #bbb;
    border-radius: 3px;
    background: #e6e6e6;
    box-shadow: inset 0 1px 0 rgba(255, 255, 255, .5), inset 0 15px 17px rgba(255, 255, 255, .5), inset 0 -5px 12px rgba(0, 0, 0, .05);
    color: rgba(0, 0, 0, .8);
    font-size: 12px;
    font-size: .75rem;
    line-height: 1;
    padding: .6em 1em .4em
}

button:hover,
input[type=button]:hover,
input[type=reset]:hover,
input[type=submit]:hover {
    border-color: #ccc #bbb #aaa;
    box-shadow: none
}

button:active,
button:focus,
input[type=button]:active,
input[type=button]:focus,
input[type=reset]:active,
input[type=reset]:focus,
input[type=submit]:active,
input[type=submit]:focus {
    border-color: #aaa #bbb #bbb;
    box-shadow: inset 0 -1px 0 rgba(255, 255, 255, .5), inset 0 2px 5px rgba(0, 0, 0, .15)
}

input[type=color],
input[type=date],
input[type=datetime-local],
input[type=datetime],
input[type=email],
input[type=month],
input[type=number],
input[type=password],
input[type=range],
input[type=search],
input[type=tel],
input[type=text],
input[type=time],
input[type=url],
input[type=week],
textarea {
    color: #666;
    border: 1px solid #ccc;
    border-radius: 3px
}

select {
    border: 1px solid #ccc
}

input[type=color]:focus,
input[type=date]:focus,
input[type=datetime-local]:focus,
input[type=datetime]:focus,
input[type=email]:focus,
input[type=month]:focus,
input[type=number]:focus,
input[type=password]:focus,
input[type=range]:focus,
input[type=search]:focus,
input[type=tel]:focus,
input[type=text]:focus,
input[type=time]:focus,
input[type=url]:focus,
input[type=week]:focus,
textarea:focus {
    color: #111
}

input[type=color],
input[type=date],
input[type=datetime-local],
input[type=datetime],
input[type=email],
input[type=month],
input[type=number],
input[type=password],
input[type=range],
input[type=search],
input[type=tel],
input[type=text],
input[type=time],
input[type=url],
input[type=week] {
    padding: 3px
}

textarea {
    padding-left: 3px;
    width: 100%;
    color: #A5A5A5
}

a {
    color: #e67474;
    outline: 0;
    -webkit-transition: color .2s ease-out, border .2s ease-out, opacity .2s ease-out;
    -moz-transition: color .2s ease-out, border .2s ease-out, opacity .2s ease-out;
    transition: color .2s ease-out, border .2s ease-out, opacity .2s ease-out
}

a:active,
a:focus,
a:hover {
    color: orange
}

a:focus {
    outline: 0
}

a:active,
a:hover {
    outline: 0;
    color: orange
}

.site-top {
    width: 100%;
    display: block;
    margin: 0 auto;
    padding: 0 20px
}

@keyframes fade-in {
    0% {
        opacity: 0
    }
    40% {
        opacity: 0
    }
    100% {
        opacity: 1
    }
}

@-webkit-keyframes fade-in {
    0% {
        opacity: 0
    }
    40% {
        opacity: 0
    }
    100% {
        opacity: 1
    }
}

.wrapper {
    animation: fade-in;
    animation-duration: .5s;
    -webkit-animation: fade-in .5s
}

#bgvideo {
    position: absolute;
    top: 0;
    left: 0;
    margin: 0;
    padding: 0;
    min-width: 99.999%;
    min-height: 550px;
    z-index: 0
}

#video-add,
#video-btn {
    position: absolute;
    bottom: 3px;
    right: 5px;
    width: 32px;
    height: 32px;
    z-index: 100;
    background-position: center;
    background-size: cover;
    cursor: pointer;
    opacity: .8;
    -moz-animation: poi-face 10s linear infinite alternate;
    -webkit-animation: poi-face 10s linear infinite alternate;
    -o-animation: poi-face 10s linear infinite alternate;
    animation: poi-face 10s linear infinite alternate
}

#video-add:hover,
#video-btn:hover {
    opacity: 1
}

.loadvideo,
.video-play {
    background-image: url(https://cdn.jsdelivr.net/gh/moezx/cdn@3.1.9/img/Sakura/images/play@32x32.png)
}

.video-pause {
    background-image: url(https://cdn.jsdelivr.net/gh/moezx/cdn@3.1.9/img/Sakura/images/pause@32x32.png)
}

#video-add {
    background-image: url(https://cdn.jsdelivr.net/gh/moezx/cdn@3.1.9/img/Sakura/images/add@32x32.png);
    bottom: 45px;
    display: none
}

.video-stu {
    position: absolute;
    bottom: -100px;
    left: 0;
    right: 0;
    margin: auto;
    padding: 6px 15px;
    text-align: center;
    color: #FFF;
    width: 100%;
    background-color: rgba(0, 0, 0, .8);
    border-radius: 0;
    font-size: 18px;
    -webkit-transition: .4s ease all;
    -moz-transition: .4s ease all;
    -o-transition: .4s ease all;
    transition: .4s ease all
}

.pattern-center {
    position: relative;
    top: 0;
    left: 0;
    width: 100%;
    overflow: hidden
}

.pattern-center::before {
    content: "";
    position: absolute;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
    background-color: rgba(0, 0, 0, .3)
}

.pattern-center::after {
    content: '';
    width: 150%;
    height: 4.375rem;
    background: #fff;
    left: -25%;
    bottom: -2.875rem;
    border-radius: 100%;
    position: absolute
}

.pattern-attachment-img {
    background-repeat: no-repeat;
    background-size: cover;
    background-position: center center;
    background-origin: border-box;
    width: 100%;
    height: 400px
}

.pattern-center header.pattern-header {
    position: absolute;
    top: 45%;
    left: 0;
    right: 0;
    text-align: center;
    color: #FFF;
    z-index: 1
}

.pattern-center h1.cat-title,
.pattern-center h1.entry-title {
    color: #FFF;
    font-size: 40px;
    font-weight: 500;
    width: 80%;
    margin: auto;
    padding: 0;
    border: 0
}

.pattern-center .cat-des {
    font-size: 16px;
    display: block
}

.single-center::after {
    display: none
}

.pattern-center header.single-header {
    max-width: 800px;
    padding: 0 10px;
    margin-left: auto;
    margin-right: auto;
    text-align: left;
    top: auto;
    bottom: 20px
}

.single-center .single-header h1.entry-title {
    margin: 0;
    font-size: 32px;
    width: 100%
}

.single-center .entry-census {
    color: #fff;
    font-size: 14px;
    padding: 18px 0 0;
    line-height: 39px
}

.single-center .entry-census .bull {
    margin: 0 5px
}

.single-center .entry-census span img {
    width: 35px;
    height: 35px;
    border-radius: 100%;
    float: left;
    margin-right: 12px
}

.single-center .entry-census a {
    color: #fff
}

@media (max-width:860px) {
    .pattern-center header.single-header {
        padding: 0 4%
    }
    .pattern-attachment-img {
        height: 280px
    }
    .pattern-center h1.cat-title,
    .pattern-center h1.entry-title,
    .single-center .single-header h1.entry-title {
        font-size: 26px
    }
}

.pattern-center-sakura {
    position: relative;
    top: 0;
    left: 0;
    width: 800px;
    overflow: hidden;
    margin: 0 auto
}

.pattern-center-blank {
    padding-top: 75px;
    background-color: #fff
}

@media (max-width:860px) {
    .pattern-center-sakura {
        position: relative;
        top: 0;
        left: 0;
        width: 100%;
        overflow: hidden
    }
    .pattern-center-blank {
        padding-top: 0
    }
}

.pattern-center-sakura::before {
    content: "";
    position: absolute;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
    background-color: rgba(0, 0, 0, .3)
}

.pattern-attachment-img {
    background-repeat: no-repeat;
    background-size: cover;
    background-position: center center;
    background-origin: border-box;
    width: 100%;
    height: 400px
}

.pattern-center-sakura header.pattern-header {
    position: absolute;
    top: 45%;
    left: 0;
    right: 0;
    text-align: center;
    color: #FFF;
    z-index: 1
}

.pattern-center-sakura h1.cat-title,
.pattern-center-sakura h1.entry-title {
    color: #FFF;
    font-size: 40px;
    font-weight: 500;
    width: 80%;
    margin: auto;
    padding: 0;
    border: 0
}

.pattern-center-sakura .cat-des {
    font-size: 16px;
    display: block
}

.single-center::after {
    display: none
}

.pattern-center-sakura header.single-header {
    max-width: 800px;
    padding: 0 10px;
    margin-left: auto;
    margin-right: auto;
    text-align: left;
    top: auto;
    bottom: 20px
}

.single-center .single-header h1.entry-title {
    margin: 0;
    font-size: 32px;
    width: 100%
}

.single-center .entry-census {
    color: #fff;
    font-size: 14px;
    padding: 18px 0 0;
    line-height: 39px
}

.single-center .entry-census .bull {
    margin: 0 5px
}

.single-center .entry-census span img {
    width: 35px;
    height: 35px;
    border-radius: 100%;
    float: left;
    margin-right: 12px
}

.single-center .entry-census a {
    color: #fff
}

@media (max-width:860px) {
    .pattern-center-sakura header.single-header {
        padding: 0 4%
    }
    .pattern-attachment-img {
        height: 280px
    }
    .pattern-center-sakura h1.cat-title,
    .pattern-center-sakura h1.entry-title,
    .single-center .single-header h1.entry-title {
        font-size: 26px
    }
}

.site-header {
    width: 100%;
    height: 75px;
    background: 0 0;
    -webkit-transition: all .4s ease;
    transition: all .4s ease;
    position: fixed;
    z-index: 9999;
    top: 0
}

.yya {
    position: fixed;
    left: 0;
    background: rgba(255, 255, 255, .95);
    box-shadow: 0 1px 40px -8px rgba(0, 0, 0, .5)
}

.gizle {
    top: -100px;
    top: 0;
    z-index: 9999
}

.site-header:hover {
    background-color: #fff
}

.sabit {
    top: 0;
    z-index: 9999
}

.site-branding {
    float: left;
    position: relative;
    height: 75px;
    line-height: 75px;
    margin-left: -6px
}

.site-title {
    margin: 0
}

.site-title a {
    color: #464646;
    font-weight: 400
}

.site-title a:hover {
    color: orange
}

.site-title img {
    height: 40px;
    margin-top: 17px;
    opacity: .75;
    -webkit-transition: color .2s ease-out, border .2s ease-out, opacity .2s ease-out;
    -moz-transition: color .2s ease-out, border .2s ease-out, opacity .2s ease-out;
    transition: color .2s ease-out, border .2s ease-out, opacity .2s ease-out
}

.site-title img:hover {
    opacity: 1
}

.site-description {
    float: right
}

.header-focus {
    height: 250px;
    background: #F9F9F9;
    margin: 0 auto
}

.openNav {
    display: none
}

#mo-nav {
    display: none
}

.site-top .lower {
    position: relative;
    display: inline-block;
    float: right;
    margin: 15px 0 0 0;
    font-size: 16px
}

@media (max-width:860px) {
    .site-top .lower nav ul li .animated-hover i {
        display: inline
    }
}

.site-top .lower nav {
    position: relative;
    float: right;
    display: none;
    animation: searchbox .2s
}

.site-top .lower nav.navbar {
    display: block
}


.m-nav {
    display: none
}

.site-top ul {
    margin: 0;
    padding: 0;
    list-style: none;
    display: inline-block
}

.site-top ul li {
    float: left;
    margin-left: 28px;
    position: relative;
    -webkit-transition: all 1s ease
}

.site-top ul li a {
    padding: 10px 0;
    display: inline-block;
    color: #666
}

.site-top ul li a:hover {
    color: orange
}

.site-top ul li a:after {
    content: "";
    display: block;
    position: absolute;
    bottom: -16px;
    height: 6px;
    background-color: #FE9600;
    width: 100%;
    max-width: 0;
    transition: max-width .25s ease-in-out
}

.site-top ul li ul li a:after {
    content: "";
    display: none;
    position: absolute;
    bottom: -21px;
    height: 6px;
    background-color: #FE9600;
    width: 100%;
    max-width: 0;
    transition: max-width .25s ease-in-out
}

.site-top ul li a:hover:after {
    max-width: 100%
}

@media(max-width:1200px) {
    .site-top .lower nav{
        right: calc(-150px - 100%);
        position: absolute;
        float: right;
        animation: searchbox .2s;
        min-width: 860px;
        z-index: -1
    }
    .site-top .lower nav.navbar {
        right: calc(-50px - 100%);
    }
    .site-top .lower nav.navbar ul {
        background: #fff;
    }
}

#show-nav {
    position: relative;
    float: right;
    margin-left: 20px;
    display: block;
    width: 30px;
    height: 33px;
    margin-top: 4.5px;
    cursor: pointer;
    z-index: 999
}

#show-nav .line {
    position: absolute;
    top: 7px;
    left: 50%;
    width: 28px;
    margin-left: -15px;
    height: 2px;
    background: #666;
    -webkit-transition: all .2s ease
}

#show-nav.showNav .line1 {
    top: 16px
}

#show-nav.showNav .line3 {
    top: 25px
}

#show-nav.hideNav .line1 {
    top: 16px;
    -webkit-transform: rotateZ(45deg)
}

#show-nav.hideNav .line2 {
    opacity: 0
}

#show-nav.hideNav .line3 {
    top: 16px;
    -webkit-transform: rotateZ(-45deg)
}

.lower li ul {
    display: none;
    opacity: 1;
    position: absolute;
    background: #fff;
    padding: 10px;
    top: 46px;
    right: -13px;
    min-width: 80px;
    text-align: center;
    white-space: nowrap;
    z-index: 9999;
    border-radius: 5px;
    box-shadow: 0 1px 40px -8px rgba(0, 0, 0, .5);
    -webkit-animation: fadeInUp .3s .1s ease both;
    -moz-animation: fadeInUp .3s .1s ease both
}

.lower li ul::before {
    content: "";
    position: absolute;
    top: -20px;
    left: 50%;
    margin-left: -10px;
    border-width: 10px;
    border-style: solid;
    border-color: transparent transparent #fff transparent
}

@-webkit-keyframes fadeInUp {
    0% {
        opacity: 0;
        -webkit-transform: translateY(10px)
    }
    100% {
        opacity: 1;
        -webkit-transform: translateY(0)
    }
}

@-moz-keyframes fadeInUp {
    0% {
        opacity: 0;
        -moz-transform: translateY(10px)
    }
    100% {
        opacity: 1;
        -moz-transform: translateY(0)
    }
}

