.download {
    color: #fff !important;
    -webkit-transition: all .3s;
    -moz-transition: all .3s;
    -o-transition: all .3s;
    transition: all .3s;
    position: relative;
    border: 1px solid #72C5A6;
    overflow: hidden;
    display: inline-block;
    background: #72C5A6
}

