    border-bottom-color: rgba(255, 255, 255, 1);
    background-color: rgba(255, 255, 255, .25)
}

.download::after {
    -webkit-transition-delay: .2s;
    transition-delay: .2s
}

.download:hover::after,
.download:hover::before {
