    z-index: 3;
    width: 400px;
    -webkit-transition: .3s;
    transition: all .3s;
    background-color: rgba(255,255,255,0.8);
    box-shadow: 0 1px 20px -8px rgba(0,0,0,.5);
    z-index: 50;
}

.widget-area.active{
    left: -1px !important;
}

.widget-area .sakura_widget{
    padding: 16px;
    height: calc(100% - 30px);
    overflow-y: auto;
}

.widget-area .sakura_widget h2 {
    border-bottom: 1px solid #000;
}

.widget-area .sakura_widget ol,.widget-area .sakura_widget ul {
    margin: 0;
    padding: 0;
    list-style: none;
}

.widget-area .heading{
    color: #555;
    font-size: 20px;
    font-weight: 900;
    text-align: center;
    height: 30px;
    background-color: #f5f5f5;
    border-color: #ddd;
}

.widget-area .show-hide-wrap{
    position: absolute;
    background: #e6e6e6;
    right: -18px;
    width: 18px;
    height: 66px;
    border-radius: 0 6px 6px 0;
    z-index: 50;
}

.active .show-hide-wrap {
    width: 100%;
    right: 0;
    transition: all .3s ease;
}

.widget-area .show-hide{
    display: inline-block;
    width: 100%;
    height: 100%;
    font-size: 12px;
    outline:none;
    box-shadow: none;
    border: none;
    opacity: .8;
    vertical-align: middle;
    padding: 0;
    margin: 0;
    -webkit-transform: rotateY(0);
    transform: rotateY(0);
    cursor: pointer;
    border-radius: 0 6px 6px 0;
    transition: all .3s ease;
}

.widget-area.active .show-hide{
    -webkit-transform: rotateY(180deg);
    transform: rotateY(180deg);
}

.widget-area .show-hide svg{
    width: 100%;
    height: 100%;
}
.bangumi {
    margin-top: 40px;
}
.bangumi .row {
    display: flex;
    margin: 0 -10px -20px;
    flex-wrap: wrap;
}
.bangumi .column {
    max-width: 50%;
    flex: 0 0 50%;
    margin-bottom: 30px;
    padding: 0 15px;
    max-width: 100%;
    flex: 0 0 100%;
    transition: .5s;
}

.bangumi-item {
    height: 0;
    color: #fff;
    display: block;
    overflow: hidden;
    text-align: center;
    position: relative;
    padding-bottom: 130%;
    box-shadow: 0 0 10px rgba(0, 0, 0, .1), 0 5px 20px rgba(0, 0, 0, .2);
}
.bangumi-item:hover {
    color: #fff;
}
.bangumi-item img {
    width: 100%;
    user-select: none;
    object-fit: cover;
    transition: filter 2s;
}
.bangumi-item .bangumi-info {
    height: 30%;
    top: 0;
    left: 0;
    right: 0;
    padding: 10px;
    position: absolute;
    background: rgba(0, 0, 0, .5);
    transition: transform 1s;
    transform: translateY(250%);
}
.bangumi-title {
    overflow: hidden;
    white-space: nowrap;
    text-overflow: ellipsis;
    margin-top: 0;
}
.bangumi-summary {
    height: 65%;
    white-space: normal;
    display: none;
    font-weight: bold;
}
.bangumi-status-bar {
    top: 0;
    bottom: 0;
    max-width: 100%;
    position: absolute;
    background: #dc143c;
}
.bangumi-status, .bangumi-status p {
    position: relative;
}
.bangumi-status {
    background: rgba(0, 0, 0, .6);
}

@media screen and (min-width: 400px) {
    .bangumi .column {
        max-width: 50%;
        flex: 0 0 50%;
    }
    .bangumi-item .bangumi-info{
        height: 50%;
        transform: translateY(140%);
    }
    .bangumi-title {
        height: 20%;
    }
}
@media screen and (min-width: 600px) {
    .bangumi .column {
        max-width: 33.3333%;
        flex: 0 0 33.3333%;
    }
    .bangumi-title {
        height: 30%;
    }
    .bangumi-item .bangumi-info{
        height: 45%;
        transform: translateY(140%);
    }
}
@media screen and (min-width: 900px) {
    .bangumi .column {
        max-width: 25%;
        flex: 0 0 25%;
    }
    .bangumi-item .bangumi-info{
        height: 100%;
        transform: translateY(85%);
    }
    .bangumi-item:hover .bangumi-info {
        transform: translateY(0);
    }
    .bangumi-item:hover img{
        filter: blur(3px);
    }
    body.dark .bangumi-item:hover img{
        filter: brightness(0.8) blur(3px);
    }
    .bangumi-title {
        height: 15%;
    }
    .bangumi-summary{
        display: block;
    }
    .bangumi-status {
        height: 10%;
    }
}
@media screen and (min-width: 1200px) {
    .bangumi-item .bangumi-info{
        height: 75%;
        transform: translateY(115%);
    }
    .bangumi-item:hover .bangumi-info {
        transform: translateY(35%);
    }
    .bangumi-title {
        height: 10%;
    }
}

#bangumi-pagination {
    width: 100%;
    margin-top: 80px;
    padding: 20px 0;
    font-size: 36px;
    text-align: center;
}

.bangumi-next{
    font-family: sans-serif;
    color: #e67474;
    position: relative;
    padding: 13px 35px;
    overflow: hidden;
}

.bangumi-next:before {
    content: ' ';
    background-color: pink;
    bottom: 0;
    left: 0;
    width: 100%;
    height: 0;
    position: absolute;
    transition: all 5s;
    z-index: -1;
}
.bangumi-next.loading:before{
    display: none !important;
}
.bangumi-next:hover:before {
    height: 100%;
}
.bangumi-next i {
    color: orange;
}
