        "tightenco/ziggy": "^2.0.0"
    },
    "require-dev": {
        "fakerphp/faker": "^1.9.1",
        "laravel/pint": "^1.2",
        "mockery/mockery": "^1.4.4",
        "nunomaduro/collision": "^8.1",
