}

optgroup {
  color: inherit;
  font: inherit;
  margin: 0;
  font-weight: 700;
}

select {
  color: var(--theme-skin)
    /*!coveredinherit*/
  ;
  font: inherit;
  margin: 0;
  text-transform: none;
  font-size: 15px;
  line-height: 1.5;
  border: 1px solid #ccc;
  font-weight: var(--global-font-weight);

}

textarea {
  color: #A5A5A5
    /*!coveredinherit,var(--theme-skin),#666*/
  ;
  font: inherit;
  margin: 0;
  overflow: auto;
  font-size: 15px;
  line-height: 1.5;
  border: 1px solid var(--theme-skin, #505050);
  border-radius: 10px;
  padding-left: 3px;
  width: 100%;
  font-weight: var(--global-font-weight);

}

input[type=reset] {
  -webkit-appearance: button;
  appearance: button;
  cursor: pointer;
  border: 1px solid;
  border-color: #ccc #ccc #bbb;
  border-radius: 3px;
  background: #e6e6e6;
  box-shadow: inset 0 1px 0 rgba(255, 255, 255, .5), inset 0 15px 17px rgba(255, 255, 255, .5), inset 0 -5px 12px rgba(0, 0, 0, .05);
  color: rgba(0, 0, 0, .8);
  font-size: .75rem
    /*!covered12px*/
  ;
  line-height: 1;
  padding: .6em 1em .4em;
  font-weight: var(--global-font-weight);

}

input[type=submit] {
  -webkit-appearance: button;
  appearance: button;
  cursor: pointer;
  border: 1px solid;
  border-color: #ccc #ccc #bbb;
  border-radius: 3px;
  background: #e6e6e6;
  box-shadow: inset 0 1px 0 rgba(255, 255, 255, .5), inset 0 15px 17px rgba(255, 255, 255, .5), inset 0 -5px 12px rgba(0, 0, 0, .05);
  color: rgba(0, 0, 0, .8);
  font-size: .75rem
    /*!covered12px*/
  ;
  line-height: 1;
  padding: .6em 1em .4em;
  font-weight: var(--global-font-weight);

}

table {
  border-collapse: collapse;
  border-spacing: 0;
  margin: 0 0 1.5em;
  width: 100%;
}

blockquote {
  margin: 0;
  padding: 30px 60px;
  position: relative;
  quotes: "" "";
}

blockquote:before {
  content: "\f10d" !important;
  font-size: 3rem;
  position: absolute;
  top: -25px;
  left: 12px;
  color: var(--theme-skin, #505050);
  font-family: FontAwesome;
  font-weight: var(--global-font-weight);
}

blockquote:after {
  content: '\f10e' !important;
  font-size: 3rem;
  position: absolute;
  bottom: -25px;
  right: -5px;
  color: var(--theme-skin, #505050);
  font-family: FontAwesome;
  font-weight: var(--global-font-weight);
}

input[type=button] {
  border: 1px solid;
  border-color: #ccc #ccc #bbb;
  border-radius: 3px;
  background: #e6e6e6;
  box-shadow: inset 0 1px 0 rgba(255, 255, 255, .5), inset 0 15px 17px rgba(255, 255, 255, .5), inset 0 -5px 12px rgba(0, 0, 0, .05);
  color: rgba(0, 0, 0, .8);
  font-size: .75rem
    /*!covered12px*/
  ;
  line-height: 1;
  padding: .6em 1em .4em;
  font-weight: var(--global-font-weight);

}

input[type=color] {
  color: #666;
  border: 1px solid var(--theme-skin, #505050);
  border-radius: 10px;
  padding: 3px;
}

