.site-top {
  width: 100%;
  display: block;
  margin: 0 auto;
  padding: 0 20px;
}

.wrapper {
  animation: blur .8s;
  -webkit-animation: blur .8s;
  background-color: rgba(255, 255, 255, .8);
  transition: all 0.6s ease-in-out;
}

#bgvideo {
  position: absolute;
  top: 0;
  left: 0;
  margin: 0;
  padding: 0;
  min-height: 550px;
  z-index: 0;
  object-fit: cover;
  width: 100%;
  height: 100%;
}

#video-add {
  position: absolute;
  bottom: 45px
    /*!covered3px*/
  ;
  right: 5px;
  width: 32px;
  height: 32px;
  z-index: 100;
  background-position: center;
  background-size: cover;
  cursor: pointer;
  opacity: .8;
  -moz-animation: poi-face 10s linear infinite alternate;
  -webkit-animation: poi-face 10s linear infinite alternate;
  -o-animation: poi-face 10s linear infinite alternate;
  animation: poi-face 10s linear infinite alternate;
  display: none;
}

#video-btn {
  position: absolute;
  bottom: 3px;
  right: 5px;
  width: 32px;
  height: 32px;
  z-index: 100;
  background-position: center;
  background-size: cover;
  cursor: pointer;
  opacity: .8;
  -moz-animation: poi-face 10s linear infinite alternate;
  -webkit-animation: poi-face 10s linear infinite alternate;
  -o-animation: poi-face 10s linear infinite alternate;
  animation: poi-face 10s linear infinite alternate;
}

.video-stu {
  position: absolute;
  bottom: -100px;
  left: 0;
  right: 0;
  margin: auto;
  padding: 6px 15px;
  text-align: center;
  color: #FFF;
  width: 100%;
  background-color: rgba(0, 0, 0, .8);
  border-radius: 0;
  font-size: 18px;
  -webkit-transition: .4s ease-in-out all;
  -moz-transition: .4s ease-in-out all;
  -o-transition: .4s ease-in-out all;
  transition: .4s ease-in-out all;
  font-weight: var(--global-font-weight);

}

.pattern-center::before {
  content: "";
  position: absolute;
  top: 0;
  bottom: 0;
  left: 0;
  right: 0;
  background:linear-gradient( 
    #fff0, 
    #00000080
  );
  backdrop-filter: saturate(150%) blur(1px);
  transition: all 0.6s ease-in-out;
}

.pattern-center::after {
  content: '';
  width: 150%;
  height: 4.375rem;
  background: #fff;
  left: -25%;
  bottom: -2.875rem;
  border-radius: 100%;
  position: absolute;
}

.pattern-attachment.bg {
  background-repeat: no-repeat;
  background-size: cover;
  background-position: center center;
  background-origin: border-box;
}

.pattern-center header.pattern-header {
  position: absolute;
  top: 45%;
  left: 0;
  right: 0;
  text-align: center;
  color: #FFF;
  z-index: 1;
}

.pattern-center h1.cat-title {
  color: #FFF;
  font-size: 40px;
  font-weight: var(--global-font-weight, 500);
  width: 80%;
  margin: auto;
  padding: 0;
  border: 0;
}

.pattern-center h1.entry-title {
  color: #FFF;
  font-size: 40px;
  font-weight: var(--global-font-weight, 500);
  width: 80%;
  margin: auto;
  padding: 0;
  border: 0;
}

.pattern-center header.single-header {
  max-width: 800px;
  padding: 0 10px;
  margin-left: auto;
  margin-right: auto;
  text-align: left;
  top: auto;
  bottom: 30px;
}

.single-center .single-header h1.entry-title {
  margin: 0;
  margin-left: 5px;
  font-size: 32px;
  width: 100%;
  font-weight: var(--global-font-weight);
}

.single-center .entry-census {
  color: #fff;
  font-size: 13px;
  padding: 12px 0 0;
  line-height: 30px;
  font-weight: var(--global-font-weight);
}

.single-center .entry-census i{
  display: none;
}

.single-center .entry-census a:hover{
  color:var(--article-theme-highlight,var(--theme-skin-matching));
}

.single-center .entry-census span img {
  width: 20px;
  height: 20px;
  border-radius: 100%;
  float: left;
  margin: 4.5px 8px;
  max-height: unset;
}

#moblieGoTop {
  position: fixed;
  bottom: 65px;
  right: 10px;
  font-size: 16px;
  z-index: 99;
  border: 0;
  outline: 0;
  box-shadow: 0 1px 30px -4px #e8e8e8;
  background: rgba(255, 255, 255, 0.65);
  color: #7D7D7D;
  padding: 6px 16px;
  border-radius: var(--style_menu_radius, 10px);
  transform: scale(0);
  transition: all 0.6s ease-in-out;
  border: 1px solid #FFFFFF;
}

#moblieGoTop i {
  cursor: pointer;
}

#changskin {
  position: fixed;
  bottom: 15px;
  right: 10px;
  z-index: 99;
  border: 0;
  outline: 0;
  box-shadow: 0 1px 30px -4px #e8e8e8;
  background: rgba(255, 255, 255, 0.65);
  color: #7D7D7D;
  padding: 15px;
  border-radius: var(--style_menu_radius, 10px);
  transform: scale(0);
  transition: all 0.6s ease-in-out;
  border: 1px solid #FFFFFF;
  --fa-animation-duration: 3s;
}

#changskin i {
  cursor: pointer;
}

#moblieGoTop:hover,#changskin:hover {
  color: var(--theme-skin-matching);
  box-shadow: 0 1px 20px 10px #e8e8e8;
  background: rgba(255, 255, 255, 0.85);
}

