.pattern-center-sakura header.single-header {
  max-width: 800px;
  padding: 0 10px;
  margin-left: auto;
  margin-right: auto;
  text-align: left;
  top: auto;
  bottom: 20px;
}

.site-header {
  width: 95%;
  height: 60px;
  -webkit-transition: all 1s ease-in-out !important;
  transition: all 1s ease-in-out !important;
  position: fixed;
  left: 2.5%;
  top: 2.5%;
  z-index: 999;
  border-radius: 20px;
  word-break: keep-all;
  border: 1.5px solid transparent;
}

.site-header.yya {
  background: rgba(255, 255, 255, .7);
  border-bottom: 1.5px solid #FFFFFF;
  transition: all 0.6s ease-in-out;
  width: 100%;
  left: 0;
  top: 0;
  border-radius: 0px !important;
}

.yya {
  transition: all 0.6s ease-in-out;
}

.site-title img {
  height: 40px;
  margin-top: 10px;
  opacity: 1;
  -webkit-transition: color .2s ease-out, border .2s ease-out, opacity .2s ease-out;
  -moz-transition: color .2s ease-out, border .2s ease-out, opacity .2s ease-out;
  transition: color .2s ease-out, border .2s ease-out, opacity .2s ease-out;
}

body.dark .site-title img {
  filter: brightness(1.1);
}

.site-top .lower nav {
  position: relative;
  float: right;
  display: none;
  animation: searchbox .4s;
}

.site-top ul {
  margin: 0;
  padding: 0;
  list-style: none;
  display: inline-block;
}

.site-top ul li {
  float: left;
  margin: 0 14px;
  position: relative;
  -webkit-transition: all 1s ease;
  transition: all 1s ease;
}

.site-top ul li a:after {
  content: "";
  display: block;
  position: absolute;
  bottom: -5px;
  height: 4px;
  background-color: var(--theme-skin-matching, #505050);
  width: 100%;
  border-radius: 30px;
  max-width: 0;
  transition: max-width 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

.site-top ul li ul li a:after {
  content: "";
  display: none;
  position: absolute;
  bottom: -21px;
  height: 4px;
  background-color: #FE9600;
  width: 100%;
  border-radius: 30px;
  max-width: 0;
  transition: max-width 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

#show-nav {
  position: relative;
  float: right;
  margin-left: 25px;
  display: block;
  width: 10px;
  height: 20px;
  margin-top: 6.5px;
  cursor: pointer;
  z-index: 999;
}

#show-nav .line {
  position: absolute;
  top: 7px;
  left: 50%;
  width: 16px;
  margin-left: -12px;
  height: 2.5px;
  background: var(--theme-skin, #505050);
  -webkit-transition: all .2s ease;
  transition: all .2s ease;
}

.lower li ul {
  display: none;
  opacity: 1;
  position: absolute;
  background: rgba(255, 255, 255, .95);
  padding: 10px;
  top: 45px;
  right: -20px;
  min-width: 80px;
  text-align: center;
  white-space: nowrap;
  z-index: 9999;
  border-radius: 15px;
  /*box-shadow: 0 1px 40px -8px rgba(0, 0, 0, .2);*/
  animation: header-menu .8s;
}

.lower li ul::before {
  content: "";
  position: absolute;
  top: -20px;
  left: 50%;
  margin-left: -10px;
  border-width: 10px;
  border-style: solid;
  border-color: transparent transparent #fff transparent;
}

.site-top .menu-item-has-children li a {
  font-size: 14px;
  padding: 8px 18px;
  text-transform: none;
  color: #545454;
  font-weight: var(--global-font-weight);

}

.site-content.notransition {
  -webkit-transition: none !important;
  -moz-transition: none !important;
  -o-transition: none !important;
  -ms-transition: none !important;
  transition: none !important;
}

.site-content {
  max-width: 860px;
  padding: 0 20px;
  margin-left: auto;
  margin-right: auto;
  background-color: transparent !important;
}

.feature i {
  position: absolute;
  top: 10px;
  left: 10px;
  font-size: 25px;
  color: #fff;
  width: 80px;
  height: 80px;
  line-height: 80px;
  text-align: center;
  background: var(--theme-skin, #505050);
  border-radius: 50%;
  opacity: 0;
  z-index: 1;
}

.overlay i {
  -moz-transition: opacity .4s ease-out;
  -o-transition: opacity .4s ease-out;
  -webkit-transition: opacity .4s ease-out;
  transition: opacity .4s ease-out;
}

.feature img {
  width: 100px;
  height: 100px;
  border-radius: 50%;
  padding: 2px;
  border: 1px solid #DADADA;
  position: relative;
  object-fit: cover;
}

.post-list h1.entry-title {
  font-size: 20px;
  font-weight: 400;
  line-height: 50px;
  margin: 0 0 0 17%;
  position: relative;
  z-index: 1;
  display: inline-block;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
  width: 70%;
}

.p-time {
  position: absolute;
  right: 0;
  top: 16px;
  font-size: 12px;
  color: #989898;
  letter-spacing: 0;
  font-weight: var(--global-font-weight);

}

.post-list p {
  min-height: 60px;
  margin: 0 0 0 17%;
  font-size: 15px;
  color: rgba(0, 0, 0, .66);
  letter-spacing: 0;
  line-height: 30px;
  font-weight: var(--global-font-weight);
  transition: all 0.6s ease-in-out;
}

.post-list-thumb:hover {
  box-shadow: 0 1px 20px 10px #e8e8e8;
}

.post-list-thumb i {
  margin-right: 5px;
  color: rgba(255, 255, 255, 0.8);
  font-size: 12px;
  transition: all 0.6s ease-in-out !important;
  -webkit-transition: all 0.6s ease-in-out !important;
}

.post-list-thumb .post-title h3 {
  color: #505050;
  text-overflow: ellipsis;
  overflow: hidden;
  font-size: 18px;
  word-wrap: break-word;
  margin: 0;
  letter-spacing: 2px;
  transition: all 0.6s ease-in-out !important;
  -webkit-transition: all 0.6s ease-in-out !important;
}

.post-list-thumb .post-title h3:hover {
  color: var(--article-theme-highlight,var(--theme-skin-matching));
  letter-spacing: 3px;
  transition: all 0.6s ease-in-out !important;
  -webkit-transition: all 0.6s ease-in-out !important;
}

.post-list-thumb .post-title h3::before{
  display: none;
}

.post-date {
  font-size: 12px;
  width: max-content;
  position: absolute;
  top: 10px;
  left: 10px;
  padding: 5px 10px 5px 10px;
  border-radius: 6px;
  color:rgba(255, 255, 255, 0.8);
  white-space: nowrap;
  font-weight: var(--global-font-weight);
  backdrop-filter: saturate(180%) blur(10px);
  background-color: rgba(255, 255, 255, 0.2);
  border: 1px solid rgba(255, 255, 255, 0.1);
  z-index: 3;
  transition: all 0.6s ease-in-out;
  -webkit-transition: all 0.6s ease-in-out;
}

.post-meta {
  font-size: 12px;
  overflow: hidden;
  text-overflow: ellipsis;
  position: absolute;
  white-space: nowrap;
  font-weight: var(--global-font-weight);
  color: rgba(255, 255, 255, 0.8)!important;
  display: inline-flex;
  gap: 4px 12px;
  flex-direction: row;
  padding: 5px 10px 5px 10px;
  border-radius: 6px;
  max-width: 70%;
  top: 10px;
  right: 10px;
  backdrop-filter: saturate(180%) blur(10px);
  background-color: rgba(255, 255, 255, 0.2);
  border: 1px solid rgba(255, 255, 255, 0.1);
  z-index: 3;
  transition: all 0.6s ease-in-out;
  -webkit-transition: all 0.6s ease-in-out;
}

.post-meta a {
  color:rgba(255, 255, 255, 0.8);
  font-size: 12px;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
  font-weight: var(--global-font-weight);
  transition: all 0.6s ease-in-out;
  -webkit-transition: all 0.6s ease-in-out;
}

.post-meta a:hover {
  color: var(--article-theme-highlight,var(--theme-skin-matching)) !important;
  transition: all 0.6s ease-in-out;
  -webkit-transition: all 0.6s ease-in-out;
}

.post-meta .meta-author,.entry-census .meta-author{
  margin:0;
}

.post-title {
  background-color: #ffffffbd;
  margin: 5px;
  padding: 10px 15px;
  bottom: 22%;
  left: 2%;
  border: 1px solid rgba(255, 255, 255, 0.8);
  backdrop-filter: saturate(180%) blur(10px);
  -webkit-backdrop-filter: saturate(180%) blur(10px);
  position: absolute;
  display: inline-flex;
  box-shadow: 0 1px 30px -4px #e8e8e8;
  transition: all 0.6s ease-in-out;
  -webkit-transition: all 0.6s ease-in-out;
  z-index: 3;
  height: fit-content;
  max-height: 34%;
  overflow: hidden;
  max-width: 80%;
  width: fit-content;
}

.post-title:hover{
  box-shadow: 0 1px 20px 10px rgba(232,232,232,0.3);
  background: rgba(255, 255, 255, 0.85);
  transition: all 0.6s ease-in-out;
  -webkit-transition: all 0.6s ease-in-out;
}

.post-thumb a {
  height: 300px;
  position: relative;
  display: block;
  background-repeat: no-repeat;
  background-size: cover;
  overflow: hidden;
}

.post-thumb a * {
  width: 100%;
  height: 100%;
  object-fit: cover;
  transition: all 0.6s ease-in-out !important;
  -webkit-transition: all 0.6s ease-in-out !important;
}

.post-excerpt {
  position: absolute;
  bottom: 5%;
  width: 98%;
  max-height: 15%;
  height: fit-content;
  left: 2%;
  margin: 0;
  margin-top: 5px;
  z-index: 50;
  padding: 0 10px;
  color: rgba(0, 0, 0, .66);
  transition: all 0.6s ease-in-out;
  -webkit-transition: all 0.6s ease-in-out;
  z-index: 1;
  overflow: hidden;
  font-size: 15px;
}

.ai-excerpt-tip {
  font-size: 12px;
  color:#cccccc;
  float: left;
  padding-bottom: 2px;
  margin-bottom: 0;
  margin-right: 10px;
  transition: all 0.6s ease-in-out;
  -webkit-transition: all 0.6s ease-in-out;
}

.post-top {
  color:gold !important;
  transition: all 0.6s ease-in-out;
  -webkit-transition: all 0.6s ease-in-out;
}

.post-top i {
  color:gold !important;
  transition: all 0.6s ease-in-out;
  -webkit-transition: all 0.6s ease-in-out;
}

.post-excerpt p {
  margin: 0;
  height: fit-content;
  max-height: 48px;
}

.homepage-load-animation:hover{
  background: rgba(255, 255, 255, 1);
  transition: all 0.6s ease-in-out;
  -webkit-transition: all 0.6s ease-in-out;
}

.navigator a {
  display: inline-block;
  height: 39px;
  line-height: 39px;
  margin: 20px 0;
}

.navigator i {
  font-size: 15px;
  color: #7d7d7d;
  box-shadow: 0 1px 30px -4px #e8e8e8;
  background: rgba(255, 255, 255, 0.5);
  padding: 12px 12px;
  border-radius: 10px;
  margin: 0 10px;
  font-weight: 900;
  -webkit-transition: all .2s ease-in-out;
  transition: all .2s ease-in-out;
  border: 1px solid #FFFFFF;
}

.navigator i:hover {
  box-shadow: 0 1px 20px 10px #e8e8e8;
  background: rgba(255, 255, 255, 0.8);
  color: var(--theme-skin-matching, #505050);
  -webkit-transition: all .2s ease-in-out;
  transition: all .2s ease-in-out;
}

.site-footer {
  padding: max(2%, 45px);
  background: rgba(255, 255, 255, .8);
  margin-left: auto;
  margin-right: auto;
  animation: blur .8s;
  transition: all 0.6s ease-in-out;
}

.site-info {
  text-align: center;
  font-size: 13px;
  color: #B9B9B9;
  font-weight: var(--global-font-weight);

}

.footertext .foo-logo {
  width: 30px;
  height: 30px;
  opacity: .3;
  margin: 0 auto;
  background-size: cover;
  background-position: center center;
  background-repeat: no-repeat;
  animation: poi-deg 12s infinite linear;
  -webkit-animation: poi-deg 12s infinite linear;
}

.entry-census {
  font-size: 12px;
  margin: 0;
  color: #888;
  font-weight: var(--global-font-weight);
  display: inline-flex;
  flex-wrap: wrap;
  gap:2px 8px;
  max-width: 98%;
  overflow: hidden;
  max-height: 40px;
}

.entry-census i {
  display: none;
}

.entry-census.has-splitter{
  gap:2px 5px;
}

.entry-census.has-splitter>:where(a,span):not(:last-child)::after{
content: "·";
margin-left:5px;
margin-inline-start: 5px;
}

.entry-header hr {
  width: 30%;
  height: 0px;
  border: 0;
  margin: 20px auto;
}

h1.page-title {
  font-size: 20px;
  font-weight: var(--global-font-weight, 400);
  border: 1px solid #ddd;
  padding: 10px 15px;
  color: #505050;
  margin-bottom: 30px;
  transition: all 0.6s ease-in-out;
}

.post-footer {
  padding: 30px 0;
  width: 98%;
  height: auto;
  left: 50%;
  margin-left: -49%;
  position: relative;
  float: left;
  border: none;
  margin-top: 50px;
}

.post-footer a {
  cursor: pointer;
}

.post-license {
  float: left;
  box-shadow: 0 1px 30px -4px #e8e8e8;
  background: rgba(255, 255, 255, 0.5);
  color: #7D7D7D;
  padding: 12px;
  font-size: 24px;
  position: inherit;
  border-radius: 10px;
  transition: all 0.6s ease-in-out;
  -webkit-transition: all 0.6s ease-in-out;
  margin: 5px;
  border: 1px solid #FFFFFF;
  display:flex;
  gap:3px;
}

.post-license:hover {
  box-shadow: 0 1px 20px 10px #e8e8e8;
  background: rgba(255, 255, 255, 0.8);
}

.post-tags {
  float: left;
  font-size: 13px;
  color: #7D7D7D;
  box-shadow: 0 1px 30px -4px #e8e8e8;
  background: rgba(255, 255, 255, 0.5);
  padding: 14px 18px;
  text-transform: uppercase;
  position: inherit;
  border-radius: 10px;
  font-weight: var(--global-font-weight);
  transition: all 0.6s ease-in-out;
  -webkit-transition: all 0.6s ease-in-out;
  margin: 5px;
  border: 1px solid #FFFFFF;
}

.post-tags:hover {
  box-shadow: 0 1px 20px 10px #e8e8e8;
  background: rgba(255, 255, 255, 0.8);
  transition: all 0.6s ease-in-out;
  -webkit-transition: all 0.6s ease-in-out;
}

.post-modified-time {
  float: left;
  font-size: 13px;
  color: #7D7D7D;
  box-shadow: 0 1px 30px -4px #e8e8e8;
  background: rgba(255, 255, 255, 0.5);
  padding: 14.3px 18px;
  text-transform: uppercase;
  position: inherit;
  border-radius: 10px;
  font-weight: var(--global-font-weight);
  transition: all 0.6s ease-in-out;
  -webkit-transition: all 0.6s ease-in-out;
  margin: 5px;
  border: 1px solid #FFFFFF;
}

.post-modified-time:hover {
  box-shadow: 0 1px 20px 10px #e8e8e8;
  background: rgba(255, 255, 255, 0.8);
  transition: all 0.6s ease-in-out;
  -webkit-transition: all 0.6s ease-in-out;
}

.post-squares {
  overflow: hidden;
  margin: 10px 0 0 0;
  background: #000;
  width: 97%;
  left: 50%;
  margin-left: -48.5%;
  position: relative;
  border-radius: 15px;
}

.post-squares .background {
  position: absolute;
  z-index: 50;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  opacity: .4;
  background-size: cover;
  -webkit-transition: opacity .15s ease-out;
  -moz-transition: opacity .15s ease-out;
  transition: opacity .15s ease-out;
}

.post-squares a {
  position: relative;
  display: block;
  float: left;
  overflow: hidden;
  height: 100%;
  padding: 55px 25px;
  text-align: center;
}

.post-squares article {
  position: relative;
  display: block;
  float: left;
  overflow: hidden;
  height: 100%;
  padding: 55px 25px;
  text-align: center;
}

.post-squares .category {
  font-size: 10px;
  padding: 7px 15px;
  text-transform: uppercase;
  color: #fff;
  border-radius: 90px;
  background: #7fbb00;
  font-weight: var(--global-font-weight);

}

.post-squares h3 {
  font-size: 17px;
  font-weight: var(--global-font-weight, 400);
  line-height: 30px;
  margin: 20px 0;
  color: #fff;
  overflow: hidden;
  white-space: nowrap;
  text-overflow: ellipsis;
  display: block;
}

.post-squares hr {
  width: 15%;
  height: 0;
  border: 0;
  background: rgba(255, 255, 255, .6);
}

.post-squares .label {
  font-size: 13px;
  display: block;
  text-transform: uppercase;
  color: rgba(255, 255, 255, .7);
  font-weight: var(--global-font-weight);
}

.nextprev a {
  height: 150px;
  padding: 55px 40px;
  text-align: left;
  width: 100%;
}

.author-profile .profile img {
  max-width: 100%;
  border-radius: 100%;
  float: left;
  transition: all 0.6s ease-in-out;
  -webkit-transition: all 0.6s ease-in-out;
}

.author-profile .desc {
  font-size: 13px;
  letter-spacing: 0;
  display: block;
  float: left;
  margin: 5px;
  color: #7D7D7D;
  box-shadow: 0 1px 30px -4px #e8e8e8;
  background: rgba(255, 255, 255, 0.5);
  padding: 14px 18px;
  border-radius: 10px;
  transition: all 0.6s ease-in-out;
  -webkit-transition: all 0.6s ease-in-out;
  overflow: hidden;
  border: 1px solid #FFFFFF;
}

.author-profile .desc:hover {
  box-shadow: 0 1px 20px 10px #e8e8e8;
  background: rgba(255, 255, 255, 0.8);
  transition: all 0.6s ease-in-out;
  -webkit-transition: all 0.6s ease-in-out;
}

.author-profile .desc i {
  color: #7D7D7D;
  float: none;
  margin-right: 10px;
  font-size: 16px;
  transition: all 0.6s ease-in-out;
  -webkit-transition: all 0.6s ease-in-out;
}

.author_info {
  min-width: 200px;
  max-width: 70%;
  width: fit-content;
  position: relative;
  height: 110px;
  left: 50%;
  transform: translateX(-50%);
  margin: 10% 0;
  float: left;
  display: flex;
  box-shadow: 0 1px 30px -4px #e8e8e8;
  background: rgba(255, 255, 255, 0.5);
  padding: 12px 12px;
  transition: all 0.6s ease-in-out;
  -webkit-transition: all 0.6s ease-in-out;
  border-radius: 10px;
  border: 1.5px solid #FFFFFF;
  flex-direction: column;
  font-weight: var(--global-font-weight);
}

.author_info .avatar {
  float: left;
  height: 75px;
  width: 75px;
  position: absolute;
  left: 50%;
  transform: translateX(-50%);
  top: -24%;
  display: flex;
  box-shadow: none;
}

.author_info .avatar img {
  border-radius: 100%;
  vertical-align: middle;
}

.author_info .author-center {
  position: relative;
  margin-top: 10px;
  transition: all 0.6s ease-in-out;
  -webkit-transition: all 0.6s ease-in-out;
  color: #505050;
}

.author_info .author-center h3 {
  font-weight: 700;
  font-size: 24px;
  margin: 0;
  display: inline-flex;
  position: relative;
  left: 50%;
  transform: translateX(-50%);
  overflow: hidden;
  max-height: 36px;
  height: fit-content;
}

.author_info .author-center .description {
  font-size: 14px;
  font-weight: var(--global-font-weight);
  text-align: center;
  overflow: hidden;
  max-height: 20px;
  height: fit-content;
  margin: 0 10px;
}

.mashiro-profile {
  color: #7a7b7c;
  text-align: left;
  width: 330px;
  height: 100px;
  position: relative;
  top: 70px;
  left: 50%;
  transform: translate(-50%, -50%);
  border-radius: 5px;
  background-color: #fafafa;
  box-shadow: 0 0 2rem #babbbc;
  animation: show-profile .5s forwards ease-in-out;
}

.mashiro-profile .content {
  box-sizing: border-box;
  padding: 10px;
  overflow: hidden;
  position: absolute;
  width: 100%;
  height: 100%;
  top: 0;
  left: 0;
}

.mashiro-profile .photo {
  box-sizing: border-box;
  width: 100px;
  height: 100px;
  border-radius: 50%;
  overflow: hidden;
  border: 5px solid #fafafa;
  background-color: #fafafa;
  margin-left: -50px;
  box-shadow: 0 0 .5rem #babbbc;
  animation: rotate-photo .5s forwards ease-in-out;
}

.mashiro-profile .content::before {
  content: "";
  position: absolute;
  width: 230px;
  height: 150px;
  background-color: #fef1c7;
  left: 0;
  top: -20px;
  z-index: -1;
  transform: rotate(-8deg);
}

.mashiro-profile .content .text h3 {
  font-weight: 400;
  margin: 3px 0;
  letter-spacing: .5px;
  white-space: nowrap;
}

.mashiro-profile .content .text h6 {
  font-weight: 400;
  margin: 3px 0;
  letter-spacing: .5px;
  white-space: nowrap;
}

.mashiro-profile .content .btn {
  background-color: #abc;
  width: 50px;
  height: 50px;
  position: absolute;
  right: 25px;
  top: 25px;
  border-radius: 50%;
  z-index: 1;
  cursor: pointer;
  transition-duration: .3s;
  animation: pop-btn .3s both ease-in-out .5s;
}

.mashiro-profile .content .btn span {
  width: 60%;
  height: 2px;
  position: absolute;
  background-color: #fff;
  top: 50%;
  left: 20%;
  transform: translateY(-50%);
  animation: to-hamburger .3s forwards ease-in-out;
}

.mashiro-profile .content .btn span::after {
  content: "";
  width: 100%;
  height: 2px;
  position: absolute;
  background-color: #fff;
  transition-duration: .3s;
  transform: rotate(0);
  right: 0;
  margin-top: 7px;
}

.mashiro-profile .content .btn span::before {
  content: "";
  width: 100%;
  height: 2px;
  position: absolute;
  background-color: #fff;
  transition-duration: .3s;
  transform: rotate(0);
  right: 0;
  margin-top: -7px;
}

.mashiro-profile .box {
  width: 150px;
  height: 150px;
  opacity: 0;
  border-radius: 50%;
  background-color: rgba(255, 255, 255, .7);
  position: absolute;
  top: 50%;
  right: -40%;
  transform: translate(-50%, -50%);
  transition-duration: .3s;
}

.mashiro-profile .box a {
  width: 50px;
  height: 50px;
  border-radius: 50%;
  background-color: #ececec;
  font-size: 26px;
  text-align: center;
  line-height: 50px;
