  transition: all 0.6s ease-in-out;
  -webkit-transition: all 0.6s ease-in-out;
}

body.dark .shuoshuo-title h3 {
  color: #CCCCCC;
  transition: all 0.6s ease-in-out;
  -webkit-transition: all 0.6s ease-in-out;
}

body.dark .shuoshuo-comments,body.dark .shuoshuo-date {
  color: #abababab;
  transition: all 0.6s ease-in-out;
  -webkit-transition: all 0.6s ease-in-out;
}
