{
    "name": "qossmic/deptrac",
    "description": "Deptrac is a static code analysis tool that helps to enforce rules for dependencies between software layers.",
    "keywords": [
        "static analysis"
    ],
    "license": "MIT",
    "require": {
        "php": "^8.1",
        "ext-json": "*"
    },
    "bin": [
        "bin/deptrac",
        "deptrac.phar"
    ],
    "suggest": {
        "ext-dom": "For using the JUnit output formatter"
    },
    "autoload": {
        "psr-4": {
            "Qossmic\\Deptrac\\": "src/"
        },
        "files": [
            "bootstrap.php"
        ]
    }
}
