{
    "name":         "knplabs/gaufrette",
    "type":         "library",
    "description":  "PHP library that provides a filesystem abstraction layer",
    "keywords":     ["file", "filesystem", "media", "abstraction"],
    "homepage":     "http://knplabs.com",
    "license":      "MIT",
    "authors": [
        {
            "name": "KnpLabs Team",
            "homepage": "http://knplabs.com"
        },
        {
            "name":     "The contributors",
            "homepage": "http://github.com/knplabs/Gaufrette/contributors"
        }
    ],
    "require": {
        "php": "^7.4 || ^8.0"
    },
    "conflict": {
        "microsoft/windowsazure": "<0.4.3"
    },
    "require-dev": {
        "phpspec/phpspec": "^7.0",
        "phpunit/phpunit": "~8.0",
        "mikey179/vfsstream": "v1.x-dev as 1.7.0",
        "friendsofphp/php-cs-fixer": "^3.9",
        "pedrotroller/php-cs-custom-fixer": "^2.28"
    },
    "suggest": {
        "knplabs/knp-gaufrette-bundle": "to use with Symfony",
        "ext-fileinfo": "This extension is used to automatically detect the content-type of a file in the AwsS3, OpenCloud, AzureBlogStorage and GoogleCloudStorage adapters"
    },
    "autoload": {
        "psr-0": { "Gaufrette": "src/" }
    },
    "autoload-dev": {
        "psr-0": { "Gaufrette\\Functional": "tests/" }
    },
    "extra": {
        "branch-alias": {
            "dev-master": "0.10.x-dev"
        }
    },
    "archive": {
        "exclude": [
            "/tests",
            "/phpunit.xml.dist",
            "/.env.dist",
            "/spec",
            "/Makefile",
            "/phpspec.yml",
            "/docker-compose.yml",
            "/docker",
            "/.dockerignore"
        ]
    }
}
