    ],
    resolve: {
        alias: {
            '@': '/resources/js',
        },
    },
    build: {
        // sourcemap: true,
        rollupOptions: {
            output: {
                banner: '/*! 2FAuth version ' + version + ' - Copyright (c) 2024 Bubka - https://github.com/Bubka/2FAuth */',
