{
    "name": "tightenco/jigsaw",
    "description": "Simple static sites with Laravel's Blade.",
    "keywords": ["blade", "laravel", "static", "site", "generator"],
    "license": "MIT",
    "authors": [
        {
            "name": "Adam Wathan",
            "email": "adam.wathan@gmail.com"
        },
        {
            "name": "Keith Damiani",
            "email": "keith@tighten.com"
        },
        {
            "name": "Jacob Baker-Kretzmar",
            "email": "jacob@tighten.com"
        }
    ],
    "require": {
        "php": "^8.2",
        "illuminate/collections": "^11.0",
        "illuminate/console": "^11.0",
        "illuminate/container": "^11.0",
        "illuminate/filesystem": "^11.0",
        "illuminate/support": "^11.0",
        "illuminate/view": "^11.0",
        "league/commonmark": "^2.4",
        "michelf/php-markdown": "^2.0",
        "mnapoli/front-yaml": "^2.0",
        "nunomaduro/collision": "^8.1",
        "spatie/laravel-ignition": "^2.4",
        "symfony/console": "^6.0 || ^7.0",
        "symfony/error-handler": "^6.0 || ^7.0",
        "symfony/finder": "^6.0 || ^7.0",
        "symfony/process": "^6.0 || ^7.0",
        "symfony/var-dumper": "^6.0 || ^7.0",
        "symfony/yaml": "^6.0 || ^7.0",
        "vlucas/phpdotenv": "^5.6"
    },
    "require-dev": {
        "laravel/pint": "^1.16",
        "mockery/mockery": "^1.6",
        "phpunit/phpunit": "^11.0.6"
    },
    "autoload": {
        "psr-4": {
            "TightenCo\\Jigsaw\\": "src/"
        },
        "files": [
            "src/Support/helpers.php"
        ]
    },
    "autoload-dev": {
        "psr-4": {
            "Tests\\": "tests/"
        }
    },
    "bin": [
        "jigsaw"
    ],
    "config": {
        "sort-packages": true,
        "optimize-autoloader": true
    },
    "minimum-stability": "dev",
    "prefer-stable": true
}

#!/usr/bin/env php
<?php

define('JIGSAW_START', microtime(true));

if (file_exists(__DIR__ . '/vendor/autoload.php')) {
    require __DIR__ . '/vendor/autoload.php';
}

if (file_exists(getcwd() . '/vendor/autoload.php')) {
    require getcwd() . '/vendor/autoload.php';
}

$app = new TightenCo\Jigsaw\Container;

$app->singleton(
    Illuminate\Contracts\Debug\ExceptionHandler::class,
    TightenCo\Jigsaw\Exceptions\Handler::class,
);

$app->bootstrapWith([
    TightenCo\Jigsaw\Bootstrap\HandleExceptions::class,
]);

$application = new Symfony\Component\Console\Application('Jigsaw', '1.7.4');
$application->add($app[TightenCo\Jigsaw\Console\InitCommand::class]);
$application->add(new TightenCo\Jigsaw\Console\BuildCommand($app));
$application->add(new TightenCo\Jigsaw\Console\ServeCommand($app));
$application->setCatchExceptions(false);

TightenCo\Jigsaw\Jigsaw::addUserCommands($application, $app);

$application->run();
