{
    "name": "rappasoft/laravel-livewire-tables",
    "description": "A dynamic table component for Laravel Livewire",
    "keywords": [
        "rappasoft",
        "livewire",
        "datatables",
        "tables",
        "laravel"
    ],
    "homepage": "https://github.com/rappasoft/laravel-livewire-tables",
    "license": "MIT",
    "authors": [
        {
            "name": "Anthony Rappa",
            "email": "rappa819@gmail.com",
            "role": "Developer"
        },
        {
            "name": "Joe McElwee",
            "email": "joe@lowerrocklabs.com",
            "role": "Developer"
        }
    ],
    "require": {
        "php": "^8.1|^8.2|^8.3",
        "blade-ui-kit/blade-heroicons": "^2.1",
        "illuminate/contracts": "^10.0|^11.0",
        "illuminate/support": "^10.0|^11.0",
        "livewire/livewire": "^3.0|dev-main"
    },
    "require-dev": {
        "ext-sqlite3": "*",
        "brianium/paratest": "^5.0|^6.0|^7.0|^8.0",
        "laravel/pint": "^1.10",
        "monolog/monolog": "*",
        "nunomaduro/collision": "^6.0|^7.0|^8.0",
        "larastan/larastan": "^2.6",
        "orchestra/testbench": "^7.0|^8.0|^9.0",
        "phpunit/phpunit": "^9.0|^10.0|^11.0"
    },
    "autoload": {
        "psr-4": {
            "Rappasoft\\LaravelLivewireTables\\": "src"
        }
    },
    "autoload-dev": {
        "psr-4": {
            "Rappasoft\\LaravelLivewireTables\\Tests\\": "tests"
        }
    },
    "scripts": {
        "psalm": "vendor/bin/psalm",
        "test": "vendor/bin/phpunit --colors=always",
        "test-coverage": "vendor/bin/phpunit --coverage-html coverage",
        "format": "vendor/bin/pint"
    },
    "config": {
        "sort-packages": true
    },
    "extra": {
        "laravel": {
            "providers": [
                "Rappasoft\\LaravelLivewireTables\\LaravelLivewireTablesServiceProvider"
            ]
        }
    },
    "minimum-stability": "dev",
    "prefer-stable": true
}
