{
  "name": "studio/laravel-totem",
  "description": "A Laravel package to manage your cron jobs through a beautiful dashboard",
  "keywords": [
    "laravel",
    "schedule",
    "manager",
    "Code Studio",
    "totem"
  ],
  "license": "MIT",
  "authors": [
    {
      "name": "Roshan Gautam",
      "email": "roshan.gautam@hotmail.com"
    }
  ],
  "require": {
    "php": "^8.1.0",
    "ext-json": "*",
    "doctrine/dbal": "^3.1",
    "illuminate/bus": "^9.0|^10.0|^11.0",
    "illuminate/console": "^9.0|^10.0|^11.0",
    "illuminate/contracts": "^9.0|^10.0|^11.0",
    "illuminate/database": "^9.0|^10.0|^11.0",
    "illuminate/events": "^9.0|^10.0|^11.0",
    "illuminate/notifications": "^9.0|^10.0|^11.0"
  },
  "require-dev": {
    "mockery/mockery": "^1.0",
    "orchestra/testbench" : "^8.0|^9.0",
    "phpunit/phpunit": "^9.0|^10.0|^11.0"
  },
  "suggest": {
    "nexmo/client": "Required for sms notifications."
  },
  "autoload": {
    "psr-4": {
      "Studio\\Totem\\": "src/",
      "Studio\\Totem\\Tests\\": "tests/",
      "Database\\Factories\\": "database/factories/"
    },
    "files": [
      "src/helpers.php"
    ]
  },
  "extra": {
    "component": "package",
    "branch-alias": {
      "dev-8.0": "8.0.x-dev"
    },
    "laravel": {
      "providers": [
        "Studio\\Totem\\Providers\\TotemServiceProvider"
      ]
    }
  },
  "config": {
    "sort-packages": true
  },
  "prefer-stable": true,
  "minimum-stability": "dev"
}

const elixir = require('laravel-elixir');

require('laravel-elixir-vue-2');

/*
 |--------------------------------------------------------------------------
 | Elixir Asset Management
 |--------------------------------------------------------------------------
 |
 | Elixir provides a clean, fluent API for defining some basic Gulp tasks
 | for your Laravel application. By default, we are compiling the Sass
 | file for your application as well as publishing vendor resources.
 |
 */

elixir(mix => {
  mix
    .less('totem.less', 'public/css/app.css')
    .webpack('app.js')
    .copy('resources/assets/img', 'public/img')
    .copy('resources/assets/less/img', 'public/img')
    .copy('public', '../../../public/vendor/totem');
});
