{
    "name": "zendframework/zend-code",
    "description": "Extensions to the PHP Reflection API, static code scanning, and code generation",
    "license": "BSD-3-Clause",
    "keywords": [
        "zf",
        "zendframework",
        "code"
    ],
    "support": {
        "docs": "https://docs.zendframework.com/zend-code/",
