{
    "name": "tailflow/laravel-orion",
    "description": "Orion for Laravel allows you to build a fully featured REST API based on your Eloquent models and relationships with the simplicity of Laravel as you love it.",
    "keywords": [
        "laravel",
        "api"
    ],
    "license": "MIT",
    "support": {
        "issues": "https://github.com/tailflow/laravel-orion/issues",
        "source": "https://github.com/tailflow/laravel-orion"
    },
    "authors": [
        {
            "name": "Aleksei Zarubin",
            "email": "alex@tailflow.org"
        }
    ],
    "require": {
        "php": ">=7.3",
        "ext-json": "*",
        "doctrine/dbal": "^2.9|^3.1|^4.0",
        "illuminate/bus": ">=5.7",
        "illuminate/contracts": ">=5.7",
        "illuminate/database": ">=5.7",
        "illuminate/http": ">=5.7",
        "illuminate/pagination": ">=5.7",
        "illuminate/support": ">=5.7",
        "symfony/yaml": "^5.3|^6.0|^7.0"
    },
    "require-dev": {
        "mockery/mockery": "^1.0",
        "orchestra/testbench": "^3.7|^4.0|^5.0|^6.0|^7.0|^8.0|^9.0",
        "phpunit/phpunit": "^6.0|^7.0|^8.0|^9.0|^10.0|^11.0"
    },
    "autoload": {
        "psr-4": {
            "Orion\\": "src/"
        }
    },
    "autoload-dev": {
        "psr-4": {
            "Orion\\Tests\\": "tests/",
            "Orion\\Tests\\Fixtures\\App\\": "tests/Fixtures/app/"
        }
    },
    "extra": {
        "branch-alias": {
            "dev-main": "1.0-dev"
        },
        "laravel": {
            "providers": [
                "Orion\\OrionServiceProvider"
            ],
            "aliases": {
                "Orion": "Orion\\Facades\\Orion"
            }
        }
    },
    "config": {
        "sort-packages": true
    },
    "minimum-stability": "dev",
    "prefer-stable": true
}
