{
  "presets": ["env"]
}

{
  "extends": [
    "plugin:vue/essential"
  ],
  "rules": {
    "indent": ["error", 2]
  },
  "parserOptions": {
    "ecmaVersion": 6,
    "sourceType": "module"
  }
}

{
  "rules": {
    "indentation": 2
  }
}

{
  "name": "tonik/theme",
  "description": "Tonik is a WordPress Starter Theme which aims to modernize, organize and enhance some aspects of WordPress theme development.",
  "keywords": ["tonik", "theme", "gin", "wordpress", "wordpress-starter-theme", "wordpress-theme"],
  "license": "MIT",
  "type": "wordpress-theme",
  "require": {
    "php": ">=7.0",
    "tonik/gin": "^3.0.0"
  },
  "require-dev": {
    "tonik/cli": "^3.0.0"
  }
}

/*
This file is responsible only for providing theme information that are displayed
in `Apperance > Themes`. Do not write in here any CSS declarations.

Theme Name: {{ theme.name }}
Theme URI: {{ theme.url }}
Description: {{ theme.description }}
Version: {{ theme.version }}
Author: {{ theme.author }}
Author URI: {{ theme.author.url }}
Text Domain: {{ theme.textdomain }}
License: GPLv2 License
License URI: https://wordpress.org/about/gpl/
*/
