{
  "name": "phpbu/phpbu",
  "description": "PHP Backup utility.",
  "type": "library",
  "keywords": [
    "backup",
    "mysql",
    "mongodb",
    "tar",
    "arangodb",
    "elasticsearch",
    "influxdb",
    "postgres",
    "cloud",
    "rsync",
    "sftp"
  ],
  "homepage": "https://phpbu.de/",
  "license": "MIT",
  "authors": [
    {
      "name": "Sebastian Feldmann",
      "email": "sebastian@phpbu.de",
      "role": "lead"
    }
  ],
  "support": {
    "issues": "https://github.com/sebastianfeldmann/phpbu/issues"
  },
  "funding": [
    {
      "type": "github",
      "url": "https://github.com/sponsors/sebastianfeldmann"
    }
  ],
  "autoload": {
    "psr-4": {
      "phpbu\\App\\": "src/"
    }
  },
  "autoload-dev": {
    "psr-4": {
      "phpbu\\App\\": "tests/phpbu/"
    }
  },
  "require": {
    "php": ">=8.0",
    "ext-dom": "*",
    "ext-json": "*",
    "ext-spl": "*",
    "sebastian/environment": "^3.0|^4.0|^5.0|^6.0",
    "sebastianfeldmann/cli": "^3.4",
    "symfony/process": "^3.0|^4.0|^5.0|^6.0|^7.0"
  },
  "require-dev": {
    "sebastianfeldmann/git": "^3.2",
    "sebastianfeldmann/ftp": "^0.9.2",
    "guzzlehttp/guzzle": "^5.3.4|^6.5.8|^7.5.0",
    "aws/aws-sdk-php": "^3.10",
    "kunalvarma05/dropbox-php-sdk": "^0.5",
    "phpseclib/phpseclib": "^2.0",
    "softlayer/objectstorage": "dev-master",
    "vlucas/phpdotenv": "^4.0",
    "google/apiclient":"^2.7",
    "php-opencloud/openstack": "^3.0",
    "arhitector/yandex": "^2.0",
    "microsoft/azure-storage-blob": "^1.4",
    "phpmailer/phpmailer": "^6.0",
    "google/cloud-storage": "^1.42"
  },
  "suggest": {
    "sebastianfeldmann/ftp": "Require ^0.9.2 to sync to an FTP server",
    "guzzlehttp/guzzle": "Require ^5.3.3|^6.2.1 to write logs to Telegram",
    "aws/aws-sdk-php": "Require '^3.10' to sync to Amazon S3",
    "kunalvarma05/dropbox-php-sdk": "Require '^0.2' to sync to Dropbox",
    "phpseclib/phpseclib": "Require '^2.0' to use SFTP sync",
    "softlayer/objectstorage": "Require 'dev-master' to sync to Softlayer",
    "php-opencloud/openstack": "Require ^3.0 to sync to OpenStack",
    "vlucas/phpdotenv": "Require ^3.0 or ^4.0 or ^5.0 to use the Dotenv adapter",
    "google/apiclient":"Require ^2.0 to sync to Google Drive",
    "arhitector/yandex":"Require ^2.0 to sync to Yandex Disk",
    "microsoft/azure-storage-blob": "Require ^1.4 to sync to Azure Blob Storage",
    "phpmailer/phpmailer": "Require ^6.0 to receive logs via email",
    "google/cloud-storage": "Require ^1.42 to sync to Google Cloud Storage"
  },
  "bin": [
    "phpbu"
  ],
  "extra": {
    "branch-alias": {
      "dev-master": "6.0.x-dev"
    }
  },
  "scripts": {
    "post-autoload-dump": "tools/captainhook install -f -s",
    "setup": "phive install",
    "test": "tools/phpunit",
    "analyse": "tools/phpstan analyse",
    "style": "tools/phpcs --standard=psr2 src"
  },
  "config": {
    "allow-plugins": {
      "php-http/discovery": true
    }
  }
}

{
    "$schema": "http://json-schema.org/draft-07/schema#",
    "$id": "http://www.phpbu.de/phpbu.schema.json",
    "title": "PHPBU Configuration schema",
    "type": "object",
    "required": [
        "verbose",
        "logging",
        "backups"
    ],
    "properties": {
        "verbose": {
            "$id": "#root/verbose",
            "title": "Controls the output verbosity",
            "type": "boolean",
            "examples": [
                true
            ],
            "default": false
        },
        "logging": {
            "$id": "#root/logging",
            "title": "Logging",
            "type": "array",
            "default": [],
            "items": {
                "$id": "#root/logging/items",
                "title": "Controls where output is logged",
                "type": "object",
                "required": [
                    "type",
                    "target"
                ],
                "properties": {
                    "type": {
                        "$id": "#root/logging/items/type",
                        "title": "Type indicates where stuff is logged",
                        "type": "string",
                        "default": "",
                        "examples": [
                            "Json"
                        ],
                        "enum": [
                            "Json",
                            "Mail",
                            "Webhook",
                            "Telegram",
                            "Prometheus"
                        ]
                    },
                    "target": {
                        "$id": "#root/logging/items/target",
                        "title": "Target, format depends on log type",
                        "type": "string",
                        "default": "",
                        "examples": [
                            "/tmp/backup.log.json"
                        ],
                        "pattern": "^.*$"
                    }
                }
            }
        },
        "backups": {
            "$id": "#root/backups",
            "title": "A backup of a project might consist of several specific backups (database, files, ...)",
            "type": "array",
            "default": [],
            "items": {
                "$id": "#root/backups/items",
                "title": "A specific backup",
                "type": "object",
                "required": [
                    "name",
                    "source",
                    "target"
                ],
                "properties": {
                    "name": {
                        "$id": "#root/backups/items/name",
                        "title": "Name",
                        "type": "string",
                        "default": "",
                        "examples": [
                            "Database backup"
                        ],
                        "pattern": "^.*$"
                    },
                    "source": {
                        "$id": "#root/backups/items/source",
                        "title": "Source",
                        "type": "object",
                        "required": [
                            "type",
                            "options"
                        ],
                        "properties": {
                            "type": {
                                "$id": "#root/backups/items/source/type",
                                "title": "Type",
                                "type": "string",
                                "default": "",
                                "examples": [
                                    "Tar"
                                ],
                                "enum": [
                                    "ArangoDump",
                                    "ElasticDump",
                                    "MongoDump",
                                    "MysqlDump",
                                    "PgDump",
                                    "InfluxDump",
                                    "LdapDump",
                                    "Redis",
                                    "RSync",
                                    "Tar",
                                    "XtraBackup"
                                ]
                            },
                            "options": {
                                "$id": "#root/backups/items/source/options",
                                "title": "Options for the source, content depends on type",
                                "type": "object"
                            }
                        }
                    },
                    "target": {
                        "$id": "#root/backups/items/target",
                        "title": "Target",
                        "type": "object",
                        "required": [
                            "dirname",
                            "filename"
                        ],
                        "properties": {
                            "dirname": {
                                "$id": "#root/backups/items/target/dirname",
                                "title": "Path to the directory where the backup is stored, absolute or relative to the config file.",
                                "type": "string",
                                "default": "",
                                "examples": [
                                    "/tmp"
                                ],
                                "pattern": "^.*$"
                            },
                            "filename": {
                                "$id": "#root/backups/items/target/filename",
                                "title": "Filename of output, might contain placeholders, see https://www.php.net/manual/en/function.date.php for possible values",
                                "type": "string",
                                "default": "",
                                "examples": [
                                    "backup-%Y%m%d-%H%i.sql"
                                ],
                                "pattern": "^.*$"
                            },
                            "compress": {
                                "$id": "#root/backups/items/target/compress",
                                "title": "The type of compression to use",
                                "type": "string",
                                "default": "",
                                "examples": [
                                    "bzip2"
                                ],
                                "enum": [
                                    "bzip2",
                                    "gzip",
                                    "zip",
                                    "7zip",
                                    "xz"
                                ]
                            }
                        }
                    },
                    "checks": {
                        "$id": "#root/backups/items/checks",
                        "title": "Checks applied to created backups",
                        "type": "array",
                        "default": [],
                        "items": {
                            "$id": "#root/backups/items/checks/items",
                            "title": "A specific check",
                            "type": "object",
                            "required": [
                                "type",
                                "value"
                            ],
                            "properties": {
                                "type": {
                                    "$id": "#root/backups/items/checks/items/type",
                                    "title": "The type of check",
                                    "type": "string",
                                    "default": "",
                                    "examples": [
                                        "SizeMin"
                                    ],
                                    "enum": [
                                        "SizeMin",
                                        "SizeDiffPreviousPercent",
                                        "SizeDiffAvgPercent"
                                    ]
                                },
                                "value": {
                                    "$id": "#root/backups/items/checks/items/value",
                                    "title": "The value for the check, p.ex. 500k for sizemin or 10 for 10% maximum size difference",
                                    "type": "string",
                                    "default": "",
                                    "examples": [
                                        "50M"
                                    ],
                                    "pattern": "^.*$"
                                }
                            }
                        }
                    },
                    "crypt": {
                        "$id": "#root/backups/items/crypt",
                        "title": "Specifies what type of encryption should be used to encrypt the backup.",
                        "type": "object",
                        "default": [],
                        "properties": {
                            "type": {
                                "$id": "#root/backups/items/crypts/items/type",
                                "title": "The type of sync",
                                "type": "string",
                                "default": "",
                                "examples": [
                                    "gpg"
                                ],
                                "enum": [
                                    "gpg",
                                    "mcrypt",
                                    "openssl"
                                ]
                            },
                            "skipOnFailure": {
                                "$id": "#root/backups/items/crypts/items/value",
                                "title": "You can still execute the crypt, even if failure(s) occurred.",
                                "type": "boolean",
                                "default": "false",
                                "examples": [
                                    "true"
                                ]
                            },
                            "options": {
                                "$id": "#root/backups/items/crypts/items/options",
                                "title": "The options, content depends on crypt type",
                                "type": "object"
                            }
                        }
                    },
                    "syncs": {
                        "$id": "#root/backups/items/syncs",
                        "title": "Syncs allow for finished backups to be synced to some other location",
                        "type": "array",
                        "default": [],
                        "items": {
                            "$id": "#root/backups/items/syncs/items",
                            "title": "A specific sync",
                            "type": "object",
                            "required": [
                                "type"
                            ],
                            "properties": {
                                "type": {
                                    "$id": "#root/backups/items/syncs/items/type",
                                    "title": "The type of sync",
                                    "type": "string",
                                    "default": "",
                                    "examples": [
                                        "rsync"
                                    ],
                                    "enum": [
                                        "Amazons3",
                                        "Amazons3-v3",
                                        "Amazons3-v2",
                                        "Backblazes3",
                                        "Azureblob",
                                        "Dropbox",
                                        "Ftp",
                                        "GoogleDrive",
                                        "GoogleCloudStorage",
                                        "RSync",
                                        "Sftp",
                                        "Softlayer",
                                        "Openstack",
                                        "Yandex-disk"
                                    ]
                                },
                                "skipOnFailure": {
                                    "$id": "#root/backups/items/checks/items/value",
                                    "title": "You can still execute the sync, even if failure(s) occurred.",
                                    "type": "boolean",
                                    "default": "false",
                                    "examples": [
                                        "true"
                                    ]
                                },
                                "options": {
                                    "$id": "#root/backups/items/syncs/items/options",
                                    "title": "The options, content depends on sync type",
                                    "type": "object"
                                }
                            }
                        }
                    },
                    "cleanup": {
                        "$id": "#root/backups/items/cleanup",
                        "title": "Tells phpbu which files to delete after a successful backup",
                        "type": "object",
                        "required": [
                            "type",
                            "options"
                        ],
                        "properties": {
                            "type": {
                                "$id": "#root/backups/items/cleanup/type",
                                "title": "Type",
                                "type": "string",
                                "default": "",
                                "examples": [
                                    "quantity"
                                ],
                                "enum": [
                                    "capacity",
                                    "outdated",
                                    "stepwise",
                                    "quantity"
                                ]
                            },
                            "options": {
                                "$id": "#root/backups/items/cleanup/options",
                                "title": "Depend on cleanup type",
                                "type": "object"
                            }
                        }
                    }
                }
            }
        }
    }
}
