{
    "name": "spatie/once",
    "description": "A magic memoization function",
    "keywords": [
        "spatie",
        "once",
        "callable",
        "memoization",
        "cache"
    ],
    "homepage": "https://github.com/spatie/once",
    "license": "MIT",
    "authors": [
        {
            "name": "Freek Van der Herten",
            "email": "freek@spatie.be",
            "homepage": "https://spatie.be",
            "role": "Developer"
        }
    ],
    "require": {
        "php": "^8.0"
    },
    "require-dev": {
        "pestphp/pest": "^1.21",
        "symfony/var-dumper": "^5.1"
    },
    "autoload": {
        "psr-4": {
            "Spatie\\Once\\": "src"
        },
        "files": [
            "src/functions.php"
        ]
    },
    "autoload-dev": {
        "psr-4": {
            "Spatie\\Once\\Test\\": "tests"
        }
    },
    "scripts": {
        "test": "vendor/bin/pest"
    },
    "config": {
        "sort-packages": true
  }
}
