{
	"extends": [
		"plugin:@wordpress/eslint-plugin/recommended"
	],
	"env": {
		"node": true
	},
	"rules": {
		"no-console": "error"
	},
	"overrides": [
		{
			"files": ["gulp/**/*.js", "node/**/*.js", "*.js"],
			"env": {
				"node": true
			},
			"rules": {
				"no-console": "off"
			}
		},
		{
			"files": ["assets/js/**/*.js"],
			"env": {
				"browser": true
			},
			"rules": {
				"no-console": "error"
			}
		}
	]
}

{
	"extends": ["@wordpress/stylelint-config"],
	"rules": {
		"color-hex-length": "short",
		"max-nesting-depth": 4,
		"rule-empty-line-before": [
			"always",
			{
				"except": ["first-nested"],
				"ignore": ["after-comment", "inside-block"]
			}
		],
		"comment-whitespace-inside": null,
		"comment-empty-line-before": null,
		"comment-pattern": null,
		"selector-class-pattern": ["^[a-z0-9]+([_-]{1,2}[a-z0-9]+)*$", {
			"message": "Class selectors should only contain lowercase letters, numbers, single/double hyphens (--), or single/double underscores (__)."
		}],
		"no-duplicate-selectors": true,
		"no-empty-source": null,
		"declaration-block-no-duplicate-properties": true,
		"block-no-empty": null,
		"media-feature-name-no-unknown": [
			true,
			{
				"ignoreMediaFeatureNames": ["/^--/"]
			}
		],
		"at-rule-no-unknown": [
			true,
			{
				"ignoreAtRules": ["media", "custom-media"]
			}
		]
	},
	"ignoreFiles": ["**/vendor/**/*.css"],
	"overrides": [
		{
			"files": ["**/*.scss"],
			"rules": {
				"scss/dollar-variable-pattern": "^[_a-z0-9-]+$",
				"scss/at-extend-no-missing-placeholder": true
			}
		}
	]
}

import {
	readFileSync,
	writeFileSync,
	readdirSync,
	existsSync,
	mkdirSync,
	statSync,
} from 'fs';
import path from 'path';
import { transform } from '@parcel/css'; // Use LightningCSS or the package you intended to use
import { paths } from './gulp/constants.js';
import { replaceInlineCSS } from './gulp/utils.js';
// Determine if running in development mode
const isDev = process.argv.includes('--dev');

// Ensure output directories exist
const ensureDirectoryExistence = (dir) => {
	if (!existsSync(dir)) {
		mkdirSync(dir, { recursive: true });
	}
};

ensureDirectoryExistence(paths.styles.dest);
ensureDirectoryExistence(paths.styles.editorDest);

// Read the contents of _custom-media.css
const customMediaCSS = readFileSync(
	path.resolve(paths.styles.srcDir, '_custom-media.css'),
	'utf8'
);

// Function to recursively inline @import statements and move them to the top
function inlineImports(filePath, seenFiles = new Set()) {
	if (seenFiles.has(filePath)) {
		return ''; // Handle circular imports by skipping already processed files
	}
	seenFiles.add(filePath);

	const css = readFileSync(filePath, 'utf8');
	const dir = path.dirname(filePath);

	let inlinedCSS = '';
	let imports = '';

	css.replace(/@import\s+["']([^"']+)["'];/g, (match, importPath) => {
		const fullPath = path.resolve(dir, importPath);
		const importCSS = inlineImports(fullPath, seenFiles);
		imports += importCSS;
		return '';
	});

	inlinedCSS = imports + css.replace(/@import\s+["']([^"']+)["'];/g, '');

	return inlinedCSS;
}

// Recursive function to find all files
const getAllFiles = (dir) => {
	const files = readdirSync(dir);
	let filelist = [];
	files.forEach((file) => {
		const filePath = path.join(dir, file);
		const fileStat = statSync(filePath);
		if (fileStat.isDirectory()) {
			filelist = filelist.concat(getAllFiles(filePath));
		} else if (file.endsWith('.css') && !file.startsWith('_')) {
			filelist.push(filePath);
		}
	});
	return filelist;
};

// Process CSS files recursively
const processCSSFile = (filePath, outputPath) => {
	let inlinedCSS = inlineImports(filePath);

	// Prepend the custom media CSS
	inlinedCSS = customMediaCSS + inlinedCSS;
	inlinedCSS = replaceInlineCSS(inlinedCSS);
	const result = transform({
		filename: filePath,
		code: Buffer.from(inlinedCSS),
		minify: !isDev,
		sourceMap: isDev,
		targets: {
			// Example: Adjust to fit your target environments
			browsers: ['>0.2%', 'not dead', 'not op_mini all'],
		},
		drafts: {
			customMedia: true,
		},
	});

	writeFileSync(outputPath, result.code);
	if (result.map) {
		writeFileSync(`${outputPath}.map`, result.map);
	}
};

// Function to process all CSS files in a directory
const processDirectory = (dir, destDir) => {
	const files = getAllFiles(dir);
	files.forEach((file) => {
		const relativePath = path.relative(dir, file);
		const outputPath = path.join(
			destDir,
			relativePath.replace('.css', '.min.css')
		);
		const outputDir = path.dirname(outputPath);
		ensureDirectoryExistence(outputDir);
		processCSSFile(file, outputPath);
	});
};

// Process main CSS directory
processDirectory(paths.styles.srcDir, paths.styles.dest);

// Process editor CSS directory
processDirectory(paths.styles.editorSrcDir, paths.styles.editorDest);

import esbuild from 'esbuild';
import { readdirSync, existsSync, mkdirSync, statSync, readFileSync } from 'fs';
import path from 'path';
import { paths, isProd } from './gulp/constants.js';
import { replaceInlineJS } from './gulp/utils.js';

// Directory paths
const srcDir = paths.scripts.srcDir;
const outDir = paths.scripts.dest;

// Ensure output directory exists
if (!existsSync(outDir)) {
	mkdirSync(outDir, { recursive: true });
}

// Recursively find all files in the source JS directory
const getAllFiles = (dir) => {
	const files = readdirSync(dir);
	let filelist = [];
	files.forEach(file => {
		const filePath = path.join(dir, file);
		const fileStat = statSync(filePath);
		if (fileStat.isDirectory()) {
			filelist = filelist.concat(getAllFiles(filePath));
		} else if (file.endsWith('.js') || file.endsWith('.ts') || file.endsWith('.tsx')) {
			filelist.push(filePath);
		}
	});
	return filelist;
};

// Get all JavaScript and TypeScript files
const files = getAllFiles(srcDir);

// Plugin to transform code using replaceInlineJS
const replaceInlineJSPlugin = {
	name: 'replaceInlineJS',
	setup(build) {
		build.onLoad({ filter: /\.(js|ts|tsx)$/ }, async (args) => {
			const filePath = args.path;
			const sourceCode = readFileSync(filePath, 'utf8');
			const transformedCode = replaceInlineJS(sourceCode);
			let loader = path.extname(filePath).slice(1);
			if(loader === 'js'){
				loader = 'jsx';
			}
			return {
				contents: transformedCode,
				loader: loader
			};
		});
	}
};

files.forEach(file => {
	const relativePath = path.relative(srcDir, file);
	const outputPath = path.join(outDir, relativePath.replace(/\.(js|ts|tsx)$/, '.min.js'));
	const outputDir = path.dirname(outputPath);

	if (!existsSync(outputDir)) {
		mkdirSync(outputDir, { recursive: true });
	}

	esbuild.build({
		entryPoints: [file],
		outfile: outputPath,
		minify: isProd,
		sourcemap: !isProd,
		bundle: true,
		target: ['es6'], // Adjust based on your target environments
		loader: {
			'.js': 'jsx',
			'.ts': 'ts',
			'.tsx': 'tsx',
		},
		plugins: [replaceInlineJSPlugin]
	}).catch(() => process.exit(1));
});

{
  "name": "wprig/wprig",
  "description": "A progressive theme development rig for WordPress.",
  "type": "wordpress-theme",
  "license": "GPL-3.0-or-later",
  "require-dev": {
    "php": ">=7.0",
    "wp-coding-standards/wpcs": "^3.1.0",
    "dealerdirect/phpcodesniffer-composer-installer": "^0.7.1",
    "squizlabs/php_codesniffer": "3.*",
    "brain/monkey": "^2",
    "phpcompatibility/php-compatibility": "^9",
    "yoast/phpunit-polyfills": "^1.0",
    "phpunit/phpunit": "^9.5",
    "rector/rector": "^2.0",
    "friendsofphp/php-cs-fixer": "^3.67"
  },
  "config": {
    "platform": {
      "php": "8.1"
    },
    "allow-plugins": {
      "dealerdirect/phpcodesniffer-composer-installer": true
    }
  },
  "autoload": {
    "psr-4": {
      "WP_Rig\\WP_Rig\\": "inc"
    }
  },
  "scripts": {
    "install-codestandards": [
      "Dealerdirect\\Composer\\Plugin\\Installers\\PHPCodeSniffer\\Plugin::run"
    ],
    "phpcbf-dev": "\"vendor/bin/phpcbf\"",
    "run-phpcbf": [
      "@phpcbf-dev"
    ],
    "phpcs-dev": "\"vendor/bin/phpcs\" -s --parallel=4 --standard=phpcs.xml.dist",
    "phpcs-config": "\"vendor/bin/phpcs\" --config-show",
    "run-phpcs": [
      "@phpcs-dev"
    ],
    "fix": "vendor/bin/rector process && vendor/bin/php-cs-fixer fix && vendor/bin/phpcbf",
    "phpunit-dev": "\"vendor/bin/phpunit\"",
    "phpunit-integration-dev": "\"vendor/bin/phpunit\" -c \"phpunit.integration.xml.dist\""
  }
}

