{
  "presets": [
    "@10up/babel-preset-default"
  ]
}

const defaultEslintrc = require('10up-toolkit/config/.eslintrc');

module.exports = {
	...defaultEslintrc,
	rules: {
		...defaultEslintrc.rules,
		'jsdoc/check-tag-names': [
			'error',
			{
				definedTags: ['filter', 'action'],
			},
		],
	},
};

{
	"*.css": [
		"10up-toolkit lint-style"
	],
	"*.js": [
		"10up-toolkit lint-js"
	],
	"*.php": [
		"./vendor/bin/phpcs --extensions=php --runtime-set testVersion 7.4- -s"
	]
}

{
  "extends": [
    "@10up/stylelint-config"
  ],
  "rules": {
    "scale-unlimited/declaration-strict-value": [
      "/color/",
      {
        "ignoreValues": ["currentColor", "inherit", "initial", "transparent", "unset"],
        "severity": "warning"
      }
    ],
    "font-family-no-missing-generic-family-keyword": null,
    "declaration-property-unit-allowed-list": null,
    "at-rule-disallowed-list": null
  }

}

{
  "name": "10up/elasticpress",
  "description": "Supercharge WordPress with Elasticsearch.",
  "type": "wordpress-plugin",
  "keywords": ["wordpress", "plugin", "elasticsearch", "elasticpress", "search"],
  "license": ["GPL-2.0-only"],
  "minimum-stability": "dev",
  "prefer-stable": true,
  "authors": [
    {
      "name": "Taylor Lovett",
      "email": "taylorl@get10up.com"
    },
    {
      "name": "10up",
      "homepage": "https://10up.com"
    },
    {
      "name": "Aaron Holbrook",
      "email": "aaron@10up.com",
      "homepage": "https://aaronjholbrook.com"
    }
  ],
  "repositories": [
    {
      "type":"composer",
      "url":"https://wpackagist.org"
    }
  ],
  "require": {
    "php": ">=7.4",
    "composer/installers": "^1.0 || ^2.0",
    "psr/container": "1.0.0"
  },
  "require-dev": {
    "10up/phpcs-composer": "dev-trunk",
    "wpackagist-plugin/woocommerce":"*",
    "phpcompatibility/phpcompatibility-wp": "*",
    "brianhenryie/strauss": "^0.14.0",
    "yoast/phpunit-polyfills": "^1.0 || ^2.0 || ^3.0",
    "phpunit/phpunit": "^9.0.0"
  },
  "scripts": {
    "lint": "phpcs . -s --runtime-set testVersion 7.4-",
    "lint-fix": "phpcbf .",
    "test": "phpunit",
    "test-single-site": "phpunit -c single-site.xml.dist",
    "setup-local-tests": "bash bin/install-wp-tests.sh ep_wp_test root password 127.0.0.1 latest true",
    "strauss": [ "vendor/bin/strauss" ],
    "post-install-cmd": [
      "@strauss"
    ],
    "post-update-cmd": [
      "@strauss"
    ]
  },
  "extra": {
    "installer-paths": {
      "vendor/{$name}/": ["type:wordpress-plugin", "type:wordpress-theme"]
    },
    "strauss": {
      "target_directory": "vendor-prefixed",
      "namespace_prefix": "ElasticPress\\Vendor_Prefixed\\",
      "classmap_prefix": "ElasticPress_Vendor_Prefixed_",
      "constant_prefix": "EP_VENDOR_"
    }
  },
  "config": {
    "allow-plugins": {
      "composer/installers": true,
      "dealerdirect/phpcodesniffer-composer-installer": true
    }
  }
}

