{
	"name": "aimeos/aimeos-base",
	"description": "Aimeos base layer for abstracting from host environments",
	"homepage": "https://aimeos.org",
	"license": "LGPL-3.0-or-later",
	"keywords": ["php", "abstration", "adapter"],
	"support": {
		"source": "https://github.com/aimeos/aimeos-base",
		"issues": "https://github.com/aimeos/aimeos-base/issues",
		"forum": "https://aimeos.org/help",
		"wiki": "https://aimeos.org/docs"
	},
	"prefer-stable": true,
	"minimum-stability": "dev",
	"require": {
		"php": "^8.0.11",
		"ext-intl": "*",
		"ext-json": "*",
		"ext-mbstring": "*",
		"ext-pcre": "*",
		"ext-PDO": "*",
		"aimeos/macro": "~1.0",
		"nyholm/psr7": "~1.2",
		"doctrine/dbal": "~3.9||~4.0",
		"psr/http-message": "~1.0||~2.0",
		"voku/portable-ascii": "~1.4||~2.0"
	},
	"require-dev": {
		"php-coveralls/php-coveralls": "~2.0",
		"squizlabs/php_codesniffer": "~3.0",
		"phpunit/phpunit": "~10.0||~11.0",
		"phing/phing": "~2.4"
	},
	"autoload": {
		"psr-4": {
			"Aimeos\\Base\\": "src/"
		},
		"classmap": [
			"src/"
		]
	},
	"autoload-dev": {
		"psr-4": {
			"Aimeos\\Base\\": "tests/"
		},
		"classmap": [
			"tests/"
		]
	}
}
