{
  "name": "wpmetabox/meta-box",
  "type": "wordpress-plugin",
  "keywords": [
    "wordpress",
    "meta-box",
    "wordpress-plugin",
    "custom-field",
    "custom-fields"
  ],
  "description": "A powerful, professional developer toolkit to create custom meta boxes and custom fields for WordPress websites.",
  "homepage": "https://metabox.io",
  "license": "GPL-2.0",
  "minimum-stability": "stable",
  "authors": [
    {
      "name": "Tran Ngoc Tuan Anh",
      "email": "anhtnt@elightup.com",
      "homepage": "https://metabox.io",
      "role": "Developer"
    }
  ],
  "config": {
    "allow-plugins": true
  },
  "autoload": {
    "psr-4": {
      "MetaBox\\": "src/"
    }
  },
  "repositories": [
    {
      "type": "git",
      "url": "https://github.com/wpmetabox/support.git"
    }
  ],
  "require-dev": {
    "phpunit/phpunit": "^11.3"
  },
  "require": {
    "meta-box/support": "dev-master"
  },
  "scripts": {
    "post-update-cmd": [
      "echo Deleting .git dirs...",
      "rm -rf vendor/**/**/.git"
    ],
    "post-install-cmd": [
      "echo Deleting .git dirs...",
      "rm -rf vendor/**/**/.git"
    ],
    "test": [
      "./vendor/bin/phpunit"
    ]
  },
  "extra": {
    "dev-files": {
      "/": [
        ".git",
        ".github/",
        ".gitattributes",
        ".gitignore",

        ".distignore",

        "*.yaml",
        "*.yml",

        "*.txt",
        "*.less",
        "*.scss",
        "*.po",

        "composer.json",
        "composer.lock",
        "package.json",
        "package-lock.json",
        "phpcs.xml",
        "pnpm-lock.yaml",
        "phpstan.neon",
        "phpunit.xml",

        ".babelrc",
        "webpack.config.js",

        ".editoconfig",
        ".vscode",
        "esbuild.js",

        "LICENSE",
        "*.md",

        "test/",
        "tests/",

        "demo.php",
        "demo/",
        "sass/",
        "vendor/"
      ]
    }
  }
}

