def skipIfUnchanged(ctx, type):
    if ("full-ci" in ctx.build.title.lower()):
        return []

    skip_step = {
        "name": "skip-if-unchanged",
        "image": OC_CI_DRONE_SKIP_PIPELINE,
        "when": {
            "event": [
                "pull_request",
            ],
        },
    }

    # these files are not relevant for test pipelines
    # if only files in this array are changed, then don't even run the "lint"
    # pipelines (like code-style, phan, phpstan...)
    allow_skip_if_changed = [
        "^.github/.*",
        "^changelog/.*",
        "CHANGELOG.md",
        "CONTRIBUTING.md",
        "LICENSE",
        "LICENSE.md",
        "README.md",
    ]

    if type == "lint":
        skip_step["settings"] = {
            "ALLOW_SKIP_CHANGED": allow_skip_if_changed,
        }
        return [skip_step]

    if type == "acceptance-tests":
        # if any of these files are touched then run all acceptance tests
        acceptance_files = [
            "^tests/acceptance/.*",
            "^tests/drone/.*",
            "^tests/data/.*",
            "^tests/TestHelpers/.*",
            "^tests/enable_all.php",
            "^tests/preseed-config.php",
            "^vendor-bin/behat/.*",
            "^apps/[a-z_]*/ajax/.*",
            "^apps/[a-z_]*/appinfo/.*",
            "^apps/[a-z_]*/bin/.*",
            "^apps/[a-z_]*/css/.*",
            "^apps/[a-z_]*/js/.*",
            "^apps/[a-z_]*/lib/.*",
            "^apps/[a-z_]*/templates/.*",
            "^apps/files_external/3rdparty/.*",
            "^core/.*",
            "^lib/.*",
            "^l10n/.*",
            "^ocm-provider/.*",
            "^ocs/.*",
            "^ocs-provider/.*",
            "^resources/.*",
            "^settings/.*",
            "composer.json",
            "composer.lock",
            "Makefile",
            "package.json",
            "package-lock.json",
            "yarn.lock",
        ]
        skip_step["settings"] = {
            "DISALLOW_SKIP_CHANGED": acceptance_files,
        }
        return [skip_step]

    if type == "unit-tests":
        # if any of these files are touched then run all unit tests
        unit_files = [
            "^tests/apps/.*",
            "^tests/core/.*",
            "^tests/data/.*",
            "^tests/docs/.*",
            "^tests/lib/.*",
            "^tests/Settings/.*",
            "^tests/TestHelpers/.*",
            "^tests/apps.php",
            "^tests/bootstrap.php",
            "^tests/enable_all.php",
            "^tests/phpunit-autotest.xml",
            "^tests/phpunit-autotest-external.xml",
            "^tests/preseed-config.php",
            "^tests/startsessionlistener.php",
            "^apps/.*",
            "^core/.*",
            "^lib/.*",
            "^l10n/.*",
            "^ocm-provider/.*",
            "^ocs/.*",
            "^ocs-provider/.*",
            "^resources/.*",
            "^settings/.*",
            "composer.json",
            "composer.lock",
            "Makefile",
            "package.json",
            "package-lock.json",
            "phpunit.xml",
            "yarn.lock",
            "sonar-project.properties",
        ]
        skip_step["settings"] = {
            "DISALLOW_SKIP_CHANGED": unit_files,
        }
        return [skip_step]

    return []

