		"squizlabs/php_codesniffer": "^3.10.2",
		"dealerdirect/phpcodesniffer-composer-installer": "^v1.0.0",
		"wp-coding-standards/wpcs": "^3.1.0",
		"elementor/elementor-editor-testing": "0.0.3",
		"justinrainbow/json-schema": "^5.2",
