        "roundcube/plugin-installer": "~0.3.5",
        "roundcube/rtf-html-php": "^2.1"
    },
    "require-dev": {
        "ergebnis/composer-normalize": "^2.13",
        "ergebnis/phpunit-slow-test-detector": "^2.11",
        "friendsofphp/php-cs-fixer": "^3.0",
        "phpstan/extension-installer": "^1.1",
