{
  "name": "GitHub Readme Streak Stats",
  "description": "üî• Stay motivated and show off your contribution streak! üåü Display your total contributions, current streak, and longest streak on your GitHub profile README.",
  "repository": "https://github.com/DenverCoder1/github-readme-streak-stats/",
  "logo": "https://i.imgur.com/Z4bDOxC.png",
  "keywords": ["github", "dynamic", "readme", "contributions", "streak", "stats"],
  "addons": [],
  "env": {
    "TOKEN": {
      "description": "GitHub personal access token obtained from https://github.com/settings/tokens/new",
      "required": true
    }
  },
  "formation": {
    "web": {
      "quantity": 1,
      "size": "basic"
    }
  },
  "buildpacks": [
    {
      "url": "https://github.com/heroku/heroku-buildpack-apt"
    },
    {
      "url": "https://github.com/DenverCoder1/heroku-buildpack-fonts-segoe-ui"
    },
    {
      "url": "heroku/php"
    }
  ]
}

{
  "name": "denvercoder1/github-readme-streak-stats",
  "description": "üî• Stay motivated and show off your contribution streak! üåü Display your total contributions, current streak, and longest streak on your GitHub profile README.",
  "keywords": [
    "github",
    "dynamic",
    "readme",
    "contributions",
    "streak",
    "stats"
  ],
  "license": "MIT",
  "version": "1.5.0",
  "homepage": "https://github.com/DenverCoder1/github-readme-streak-stats",
  "autoload": {
    "classmap": [
      "src/"
    ]
  },
  "require": {
    "php": "^8.2",
    "ext-intl": "*",
    "vlucas/phpdotenv": "^5.3"
  },
  "require-dev": {
    "phpunit/phpunit": "^11"
  },
  "scripts": {
    "start": [
      "Composer\\Config::disableProcessTimeout",
      "php -S localhost:8000 -t src"
    ],
    "test": "./vendor/bin/phpunit --testdox tests",
    "lint": "prettier --check *.md **/*.{php,md,js,css} !**/*.min.js --print-width 120",
    "lint-fix": "prettier --write *.md **/*.{php,md,js,css} !**/*.min.js --print-width 120"
  }
}

