						"InfoValues": {
							"DetailsValue": {
								"type": "string",
								"format": "uri-reference",
								"markdownDescription": "URL of a BitBucket/Github/Gitlab repository to fetch information about the package or dependency from.\n\nCan be a relative path to the location of this repository.json (e.g.: `../mypackage`).",
