						"InfoValues": {
							"DetailsValue": {
								"type": "string",
								"format": "uri-reference",
								"markdownDescription": "URL of a BitBucket/Github/Gitlab repository to fetch information about the package or library from.\n\nCan be a relative path to the location of this repository.json (e.g.: `../mypackage`).",
								"pattern": "^((https://bitbucket\\.org/[^/#?]+|\\.\\.?)/[^/#?]+(/src/[^#?]*[^/#?]|\\#tags|/)?|(https://github\\.com/[^/#?]+|\\.\\.?)/[^/#?]+(?<!\\.git)(/tree/[^#?]*[^/#?]|/)?|(https://gitlab\\.com/[^/#?]+|\\.\\.?)/[^/#?]+(?<!\\.git)(/-/tree/[^#?]*[^/#?]|/)?)$"
							},
							"NameValue": {
								"type": "string",
								"markdownDescription": "The name a package or library is installed as."
