#!/usr/bin/env python3

import common
from shell_helpers import LF

class Main(common.BuildCliFunction):
    def __init__(self):
        super().__init__(
            description='''\
Build the gem5 m5 executable.
See: https://cirosantilli.com/linux-kernel-module-cheat#gem5-m5-executable
'''
        )

    def _get_make_cmd(self):
        allowed_toolchains = ['buildroot']
        return [
            'scons', LF,
            '-C', self.env['gem5_m5_source_dir'], LF,
            '-j', str(self.env['nproc']), LF,
            'CROSS_COMPILE={}'.format(self.env['toolchain_prefix_dash']), LF,
            self.env['gem5_m5_source_dir_build'], LF
        ]

    def build(self):
        self.sh.mkdir_p(self.env['gem5_m5_build_dir'])
        # We must clean first or else the build outputs of one arch can conflict with the other.
        # I should stop being lazy and go actually patch gem5 to support out of tree m5 build...
        self.clean()
        self.sh.run_cmd(
            self._get_make_cmd(),
        )
        self.sh.mkdir_p(self.env['out_rootfs_overlay_bin_dir'])
        self.sh.cp(
            self.env['gem5_m5_source_dir_build'],
            self.env['out_rootfs_overlay_bin_dir']
        )

    def clean(self):
        self.sh.run_cmd(
            self._get_make_cmd() + ['--clean', LF],
        )

if __name__ == '__main__':
    Main().cli()

#!/usr/bin/env python3

import os

import common
from shell_helpers import LF

class Main(common.BuildCliFunction):
    def __init__(self):
        super().__init__()
        self._add_argument('--configure')
        self.add_argument(
            '--extra-config-args',
            default='',
            help='''\
Extra arguments to pass to configure
'''
        )
        self._add_argument('extra_make_args')

    def build(self):
        build_dir = self.get_build_dir()
        os.makedirs(build_dir, exist_ok=True)
        if self.env['verbose']:
            verbose = ['V=1']
        else:
            verbose = []
        if self.env['mode'] == 'userland':
            target_list = '{}-linux-user'.format(self.env['arch'])
        else:
            target_list = '{}-softmmu'.format(self.env['arch'])
        if self.env['qemu_build_type'] == 'debug':
            # https://stackoverflow.com/questions/4689136/debug-qemu-with-gdb
            build_type_cmd = ['--enable-debug', LF]
        else:
            build_type_cmd = []
        if self.env['configure']:
            self.sh.run_cmd(
                [
                    os.path.join(self.env['qemu_source_dir'], 'configure'), LF,
                    '--enable-trace-backends=simple', LF,
                    '--target-list={}'.format(target_list), LF,
                    '--enable-sdl', LF,
                ] +
                build_type_cmd +
                self.sh.shlex_split(self.env['extra_config_args']),
                extra_paths=[self.env['ccache_dir']],
                cwd=build_dir
            )
        self.sh.run_cmd(
            (
                [
                    'make', LF,
                    '-j', str(self.env['nproc']), LF,

                ] +
                verbose +
                self.sh.add_newlines(self.env['extra_make_args'])
            ),
            cwd=build_dir,
            extra_paths=[self.env['ccache_dir']],
        )

    def get_build_dir(self):
        return self.env['qemu_build_dir']

if __name__ == '__main__':
    Main().cli()

#!/usr/bin/env python3

import os
import shlex
import subprocess
import threading

from shell_helpers import LF
import common
import thread_pool

class Main(common.BuildCliFunction):
    def __init__(self, *args, **kwargs):
        if not 'description' in kwargs:
            kwargs['description'] = '''\
Build our compiled userland examples.
'''
        if not 'defaults' in kwargs:
            kwargs['defaults'] = {}
        if not 'mode' in kwargs['defaults']:
            kwargs['defaults']['mode'] = 'userland'
        super().__init__(*args, **kwargs)
        self._add_argument('--ccflags')
        self._add_argument('--force-rebuild')
        self._add_argument('--optimization-level')
        self.add_argument(
            'targets',
            default=[],
            help='''\
https://cirosantilli.com/linux-kernel-module-cheat#build-userland
''',
            nargs='*',
        )

    def build(self):
        build_dir = self.get_build_dir()
        cc_flags = [
            '-I', self.env['root_dir'], LF,
        ] + self.env['ccflags']
        extra_obj_lkmc_common = os.path.join(
            build_dir,
            self.env['common_basename_noext'] + self.env['obj_ext']
        )
        self._build_one(
            in_path=self.env['common_c'],
            out_path=extra_obj_lkmc_common,
            cc_flags=cc_flags,
            extra_deps=[self.env['common_h']],
            link=False,
        )
        with thread_pool.ThreadPool(
            self._build_one,
            nthreads=self.env['nproc'],
            submit_raise_exit=self.env['quit_on_fail'],
        ) as my_thread_pool:
            for target in self.env['targets']:
                for path, in_dirnames, in_filenames in self.sh.walk(target):
                    for in_filename in in_filenames:
                        in_ext = os.path.splitext(in_filename)[1]
                        if not in_ext in self.env['build_in_exts']:
                            continue
                        in_path = os.path.join(path, in_filename)
                        my_thread_pool.submit({
                            'cc_flags': cc_flags,
                            'extra_objs_lkmc_common': [extra_obj_lkmc_common],
                            'in_path': in_path,
                            'out_path': self.resolve_userland_executable(in_path),
                        })
        exit_status = self._handle_thread_pool_errors(my_thread_pool)
        if exit_status != 0:
            return exit_status
        if self.env['copy_overlay']:
            self.sh.copy_dir_if_update(
                srcdir=build_dir,
                destdir=os.path.join(
                    self.env['out_rootfs_overlay_lkmc_dir'],
                    self.env['out_rootfs_overlay_dir_prefix']
                ),
                filter_ext=self.env['userland_executable_ext'],
            )
        return exit_status

    def clean(self):
        if self.env['in_tree']:
            for target in self.env['targets']:
                if os.path.exists(target):
                    if os.path.isfile(target):
                        self.sh.rmrf(self.resolve_userland_executable(target))
                    else:
                        for path, dirnames, filenames in self.sh.walk(target):
                            for filename in filenames:
                                if os.path.splitext(filename)[1] in self.env['userland_out_exts']:
                                    self.sh.rmrf(os.path.join(path, filename))
        else:
            for target in self.env['targets']:
                self.sh.rmrf(self.resolve_userland_executable(target))

    def get_build_dir(self):
        return self.env['userland_build_dir']

    def setup_one_build(self):
        self.env['targets'] = self.resolve_targets(
            [self.env['userland_source_dir']],
            self.env['targets']
        )

if __name__ == '__main__':
    Main().cli()

