def open_ssh_port():

    find_ssh_port_cmd = "cat /etc/ssh/sshd_config | grep '^Port \\d*' | tail -1"
    cmd_data = mw.execShell(find_ssh_port_cmd)
    ssh_port = cmd_data[0].replace("Port ", '').strip()
    if ssh_port == '':
        ssh_port = '22'

    mw.echoInfo("SSHç«¯å£: "+ str(ssh_port))
    MwFirewall.instance().addAcceptPort(ssh_port, 'SSHè¿œç¨‹ç®¡ç†æœåŠ¡', 'port')
    return True


