// Adapted from https://github.com/cloudfoundry/bytefmt

const (
	BYTE = 1 << (10 * iota)
	KILOBYTE
	MEGABYTE
	GIGABYTE
	TERABYTE
	PETABYTE
	EXABYTE
)

var errInvalidByteSize = errors.New("invalid byte size")

// ParseBytes parses string representations (e.g. 42K or 42KB or 42KiB) to bytes
func ParseBytes(s string) (uint64, error) {
	s = strings.ToUpper(strings.TrimSpace(s))
	i := strings.IndexFunc(s, unicode.IsLetter)

	if i == -1 {
		return 0, errInvalidByteSize
	}

	num, unit := s[:i], s[i:]
	b, err := strconv.ParseFloat(num, 64)
	if err != nil || b < 0 {
		return 0, errInvalidByteSize
	}

	switch unit {
	case "E", "EB", "EIB":
		return uint64(b * EXABYTE), nil
	case "P", "PB", "PIB":
		return uint64(b * PETABYTE), nil
	case "T", "TB", "TIB":
		return uint64(b * TERABYTE), nil
	case "G", "GB", "GIB":
		return uint64(b * GIGABYTE), nil
	case "M", "MB", "MIB":
		return uint64(b * MEGABYTE), nil
	case "K", "KB", "KIB":
		return uint64(b * KILOBYTE), nil
	case "B":
		return uint64(b), nil
	default:
		return 0, errInvalidByteSize
	}
}

