from libretranslate import main


def app(*args, **kwargs):
    import sys
    sys.argv = ['--wsgi']

    for k in kwargs:
        ck = k.replace("_", "-")
        if isinstance(kwargs[k], bool):
            if kwargs[k]:
                sys.argv.append("--" + ck)
        else:
            sys.argv.append("--" + ck)
            sys.argv.append(kwargs[k])

    instance = main()

    if len(kwargs) == 0:
        return instance(*args, **kwargs)
    else:
        return instance
