        angles.append(angle3)
    else:
        print("Please enter a value less than 180°")
        angle3 = int(input())
        angles.append(angle3)

    sum_of_angles = angle1 + angle2 + angle3
    if sum_of_angles > 180 or sum_of_angles < 180:
        print("It is not a triangle!Please enter valid angles.")
