# Specify how many cores to occupy according to the parameters


def cpu():
    arg = sys.argv[2] if len(sys.argv) == 3 else sys.argv[3]
    cpu_num = cpu_count()
    cores = int(arg)
    if not isinstance(cores, int):
        print("bad args not int")
