class Node:
    def __init__(self, data):
        self.data = data
        self.next = None

class LinkedList():
    def __init__(self):
        self.head = None

    def length(self):
        curr = self.head
        count = 0
        while curr.next != None:
            count += 1
            curr = curr.next
        return count

    def add_node(self, data):
        new_node = Node(data)
        if self.head is None:
            self.head = new_node
        else:
            curr = self.head
            while curr.next != None:
                curr = curr.next
            curr.next = new_node

    def insert_at_head(self, data):
        new_node = Node(data)
        temp = self.head
        self.head = new_node
        new_node.next = temp
        del temp

    def insert(self, pos, data):
        if pos < 0 or pos > self.length():
            print("Enter valid index")
        elif pos == 0:
            self.insert_at_head(data)
            return
        elif pos == self.length()-1:
            self.add_node(data)
            return
        new_node = Node(data)
        curr_pos = 0
        prev = None
        curr = self.head
        while True:
            if pos == curr_pos:
                prev.next = new_node
                new_node.next = curr
                break
            prev = curr
            curr = curr.next
            curr_pos += 1

    def delete_head(self):
        temp = self.head
        self.head = temp.next
        del temp

    def delete_end(self):
        curr = self.head
        prev = None
        while True:
            if curr.next == None:
                prev.next = None
                del curr
                break
            prev = curr
            curr = curr.next

    def delete(self, pos):
        if pos < 0 or pos > self.length():
            print("Enter valid index")
            return
        elif pos == 0:
            self.delete_head()
            return
        elif pos == self.length()-1:
            self.delete_end()
            return
        curr = self.head
        curr_pos = 0
        prev = None
        while True:
            if curr_pos == pos:
                prev.next = curr.next
                del curr
                break
            prev = curr
            curr = curr.next
            curr_pos += 1

    def display(self):
        if self.head is None:
            print("List is empty")
        rev = []
        curr = self.head
        while curr != None:
            print(f"{curr.data} --> ", end='')
            rev.append(curr.data)
            curr = curr.next
        print()
        return rev[::-1]

def soundex_al(word):
    cap_word = word.upper()  # convert the word to uppercase

    return_val = ""
    return_val = "" + cap_word[0]  # get the first letter of the word

    # dictonary to give values to the letters
    code_dict = {"BFPV": "1", "CGJKQSXZ": "2", "DT": "3", "L": "4", "MN": "5", "R": "6"}

    # array of charactors to remove from the word
    rem_charactors = ["A", "E", "I", "O", "U", "H", "W", "Y"]

    # for loop to remove all the 0 valued charactors
    temp = ""
    for char in cap_word[1:]:
        if char not in rem_charactors:
            temp = temp + char

    # get the values from the 'code_dict' and create the soundex code
    for char in temp:
        for key in code_dict.keys():
            if char in key:
                code = code_dict[key]
                if code != return_val[-1]:  # Remove all pairs of consecutive digits.
                    return_val += code

    return_val = return_val[:4]  # crop the word to 4 charactors

    # if soundex code doen't contain 4 digits. fill it with zeros
    if len(return_val) < 4:
        for x in range(len(return_val), 4):
            return_val = return_val + "0"

    # return the value
    return return_val


