def my_found(req):
    my_dict = {
        "XXS": [42, 36, 8, 38, 24],
        "XS": (2),
        "S": (4),
        "M": (6),
        "L": (8),
        "XL": (10),
        "XXL": (12),
        "XXXL": (14),
    }
    if req[0] != "XXS":
        answ = my_dict["XXS"][req[1] - 1] + my_dict[req[0]]
    else:
        answ = my_dict["XXS"][req[1] - 1]
    return answ


