---
layout: default
---

<style type="text/css" media="screen">
  .container {
    margin: 10px auto;
    max-width: 600px;
    text-align: center;
  }
  h1 {
    margin: 30px 0;
    font-size: 4em;
    line-height: 1;
    letter-spacing: -1px;
  }
</style>

<div class="container">
  <h1>404</h1>

  <p><strong>Page not found :(</strong></p>
  <p>The requested page could not be found.</p>
</div>

@_default:
    just --list

@alex:
	bunx alex README.md

@bootstrap:
    python -m pip install --upgrade pip uv
    uv pip install --upgrade --requirement pyproject.toml

@build:
    just doctoc
    bundle exec jekyll build

@doctoc:
    bunx doctoc README.md

@down:
    docker compose down

@lint *ARGS:
    # -uv --quiet tool run curlylint _layouts/
    # -bunx awesome-lint README.md
    uv tool run --with pre-commit-uv pre-commit run {{ ARGS }} --all-files

@serve:
    # modd --file=modd.conf
    just up ""

@start *ARGS="--detach":
    just up {{ ARGS }}

@up *ARGS:
    docker compose up {{ ARGS }}

{
	# prep: "killall ruby"
	daemon +sigterm: "bundle exec jekyll serve --drafts --watch --port 4000"
}

# DEVD proxy with livereload listening on port 8000

**/*.css
**/*.html
**/*.js
!src/**
{
    daemon: "devd --modd -l http://localhost:4000"
}

