//! Windows only!

MASM::MASM(bool x86, QObject *parent) :
    Assembler(x86, parent)
{
    #ifndef Q_OS_WIN32
        QMessageBox::critical(0, tr("Error!"), tr("MASM is available on Windows only."));
    #else
