#!/usr/bin/env pwsh

$version = '0.1.161'
$name = 'languageclient'
$url = "https://github.com/autozimu/LanguageClient-neovim/releases/download/$version/$name-$version-"

$path = "$PSScriptRoot\bin\$name"

switch ($true) {
    $IsMacOS {
        # MacOS is always x86_64
        $url += 'x86_64-apple-darwin'
    }
    $IsLinux {
        # Detecting architecture is more involved on Linux
        $arch = uname -sm
        $url += switch ($arch) {
            'Linux x86_64' { 'x86_64' }
            'Linux i686' { 'i686' }
            'Linux aarch64' { 'aarch64' }
            Default { throw 'architecture not supported' }
        }
        $url += '-unknown-linux-musl'
    }
    Default {
        # Windows
        $url += if ([Environment]::Is64BitOperatingSystem) { 'x86_64' } else { 'i686' }
        $url += '-pc-windows-gnu.exe'

        # We need to tack on the .exe to the end of the download path
        $path += '.exe'
    }
}

if (Test-Path -LiteralPath $path) {
    Remove-Item -Force -LiteralPath $path
}

echo "Downloading $url ..."

if (!$IsCoreCLR) {
    # We only need to do this for Windows PowerShell
    [Net.ServicePointManager]::SecurityProtocol = [Net.SecurityProtocolType]::Tls12
}

Invoke-WebRequest -Uri $url -OutFile $path

#!/usr/bin/env sh
# Try install by
#   - download binary
#   - build with cargo

set -o nounset    # error when referencing undefined variable
set -o errexit    # exit when command fails

version=0.1.161
name=languageclient

arch=$(uname -sm)

try_curl() {
    command -v curl > /dev/null && \
        curl --fail --location "$1" --output bin/$name
}

try_wget() {
    command -v wget > /dev/null && \
        wget --output-document=bin/$name "$1"
}

download() {
    echo "Trying download bin/${name} ${version}..."
    url=https://github.com/autozimu/LanguageClient-neovim/releases/download/$version/${1}
    if (try_curl "$url" || try_wget "$url"); then
        chmod a+x bin/$name
        return
    else
        echo "Prebuilt binary is not available for:" "${arch}"
        try_build
    fi
}

try_build() {
    if command -v cargo > /dev/null; then
        echo "Trying build locally ${version} ..."
        make release
    else
        echo "cargo is not available. Abort."
        return 1
    fi
}

bin=bin/languageclient
if [ -f "$bin" ]; then
    installed_version=$($bin -V)
    case "${installed_version}" in
        *${version}*) echo "Version is equal to ${version}, skip install." ; exit 0 ;;
        *) rm -f "$bin" ;;
    esac
fi

case "${arch}" in
    "Linux x86_64") download $name-$version-x86_64-unknown-linux-musl ;;
    "Linux i686") download $name-$version-i686-unknown-linux-musl ;;
    "Linux aarch64") download $name-$version-aarch64-unknown-linux-musl ;;
    "Darwin x86_64") download $name-$version-x86_64-apple-darwin ;;
    "FreeBSD amd64") download $name-$version-x86_64-unknown-freebsd ;;
    *) echo "No pre-built binary available for ${arch}."; try_build ;;
esac

call plug#begin('~/.local/share/nvim/plugged')

Plug 'autozimu/LanguageClient-neovim', {
    \ 'branch': 'next',
    \ 'do': 'bash install.sh',
    \ }

call plug#end()

augroup filetype_rust
    autocmd!
    autocmd BufReadPost *.rs setlocal filetype=rust
augroup END

" Always draw sign column. Prevent buffer moving when adding/deleting sign.
set signcolumn=yes

let g:LanguageClient_serverCommands = {
    \ 'rust': ['rustup', 'run', 'stable', 'rls'],
    \ }
let $RUST_BACKTRACE = 1
let g:LanguageClient_loggingLevel = 'INFO'
let g:LanguageClient_virtualTextPrefix = ''
let g:LanguageClient_loggingFile =  expand('~/.local/share/nvim/LanguageClient.log')
let g:LanguageClient_serverStderr = expand('~/.local/share/nvim/LanguageServer.log')

let
  sources = import ./nix/sources.nix;
  pkgs = import sources.nixpkgs { };
  inherit (pkgs) stdenv;
in
pkgs.mkShell {
  buildInputs = (with pkgs; [
    curl
    git
    mypy
    neovim
    rustup
    rust-analyzer # For integration tests.
    tmux
    vim # For manual tests.
    vim-vint
    (with python37Packages; [
      flake8
      pynvim
      pytest
    ])
  ])
  ++ stdenv.lib.optionals stdenv.isDarwin (with pkgs.darwin.apple_sdk.frameworks; [
    CoreServices
  ])
  ++ stdenv.lib.optionals stdenv.isLinux (with pkgs; [
    cargo-release  # build error on macOS: "iconv.h" not found.
  ]);

  NIX_LDFLAGS = stdenv.lib.optionalString stdenv.isDarwin "-framework CoreFoundation";
}
