{
  lib,
  rustPlatform,
  pkg-config,
  lz4,
  libxkbcommon,
  installShellFiles,
  scdoc,
  nix-gitignore,
}: let
  version = (builtins.fromTOML (builtins.readFile ./Cargo.toml)).workspace.package.version;
  src = nix-gitignore.gitignoreSource [] ./.;
in
rustPlatform.buildRustPackage {
  pname = "swww";

  inherit src version;

  cargoLock.lockFile = ./Cargo.lock;

  buildInputs = [
    lz4
    libxkbcommon
  ];

  doCheck = false; # Integration tests do not work in sandbox environment

  nativeBuildInputs = [
    pkg-config
    installShellFiles
    scdoc
  ];

  postInstall = ''
    for f in doc/*.scd; do
      local page="doc/$(basename "$f" .scd)"
      scdoc < "$f" > "$page"
      installManPage "$page"
    done

    installShellCompletion --cmd swww \
      --bash completions/swww.bash \
      --fish completions/swww.fish \
      --zsh completions/_swww
  '';

  meta = {
    description = "Efficient animated wallpaper daemon for wayland, controlled at runtime";
    license = lib.licenses.gpl3;
    platforms = lib.platforms.linux;
    mainProgram = "swww";
  };
}

(import (let lock = builtins.fromJSON (builtins.readFile ./flake.lock);
in fetchTarball {
  url =
    "https://github.com/edolstra/flake-compat/archive/${lock.nodes.flake-compat.locked.rev}.tar.gz";
  sha256 = lock.nodes.flake-compat.locked.narHash;
}) { src = ./.; }).defaultNix

{
  description = "swww, A Solution to your Wayland Wallpaper Woes";

  # Nixpkgs / NixOS version to use.
  inputs.nixpkgs.url = "github:nixos/nixpkgs/nixos-unstable";
  inputs.utils.url = "github:numtide/flake-utils";
  inputs.flake-compat = {
    url = "github:edolstra/flake-compat";
    flake = false;
  };

  outputs = {
    self,
    nixpkgs,
    utils,
    ...
  }:
    {
      overlays.default = final: prev: {
        swww = final.callPackage ./build.nix {};
      };
    }
    // utils.lib.eachDefaultSystem (system: let
      pkgs = import nixpkgs {
        inherit system;
        overlays = [self.overlays.default];
      };
    in {
      packages = {
        inherit (pkgs) swww;
        default = pkgs.swww;
      };

      formatter = pkgs.alejandra;

      devShells.default = pkgs.callPackage ({
        mkShell,
        rustc,
        cargo,
        gnumake,
        pkg-config,
        lz4,
        libxkbcommon,
        swww,
      }:
        mkShell {
          inherit (swww) nativeBuildInputs buildInputs;
        }) {};
    });
}
