{
    "name": "svix/svix",
    "type": "library",
    "description": "Svix PHP Library",
    "keywords": ["webhooks","api"],
    "homepage": "https://www.svix.com",
    "license": "MIT",
    "authors": [
        {
            "name": "Svix",
            "email": "support@svix.com",
            "homepage": "http://www.svix.com"
        }
    ],
    "require": {
        "php": ">=5.6.0",
        "ext-json": "*"
      },
    "require-dev": {
        "phpunit/phpunit": "^5.7",
        "friendsofphp/php-cs-fixer": "^3.0",
        "phpstan/phpstan": "^0.12.89"
    },
    "autoload": {
        "psr-4": {
            "Svix\\": "php/src/"
        }
    },
    "autoload-dev": {
        "psr-4": {
            "Svix\\": [
                "php/tests/"
            ]
        }
    }
}

{
    "components": {
        "schemas": {
            "AggregateEventTypesOut": {
                "properties": {
                    "id": {
                        "type": "string"
                    },
                    "status": {
                        "$ref": "#/components/schemas/BackgroundTaskStatus"
                    },
                    "task": {
                        "$ref": "#/components/schemas/BackgroundTaskType"
                    }
                },
                "required": [
                    "id",
                    "status",
                    "task"
                ],
                "type": "object"
            },
            "AppPortalAccessIn": {
                "properties": {
                    "application": {
                        "$ref": "#/components/schemas/ApplicationIn",
                        "description": "Optionally creates a new application while generating the access link. If the application id or uid that is used in the path already exists, this argument is ignored.",
                        "nullable": true
                    },
                    "expiry": {
                        "default": 604800,
                        "description": "How long the token will be valid for, in seconds.\n\nValid values are between 1 hour and 7 days. The default is 7 days.",
                        "format": "uint64",
                        "maximum": 604800,
                        "minimum": 3600,
                        "nullable": true,
                        "type": "integer"
                    },
                    "featureFlags": {
                        "description": "The set of feature flags the created token will have access to.",
                        "example": [],
                        "items": {
                            "example": "cool-new-feature",
                            "maxLength": 256,
                            "pattern": "^[a-zA-Z0-9\\-_.]+$",
                            "type": "string"
                        },
                        "type": "array",
                        "uniqueItems": true
                    },
                    "readOnly": {
                        "description": "Whether the app portal should be in read-only mode.",
                        "nullable": true,
                        "type": "boolean"
                    }
                },
                "type": "object"
            },
            "AppPortalAccessOut": {
                "properties": {
                    "token": {
                        "example": "appsk_kV3ts5tKPNJN4Dl25cMTfUNdmabxbX0O",
                        "type": "string"
                    },
                    "url": {
                        "example": "https://app.svix.com/login#key=eyJhcHBJZCI6ICJhcHBfMXRSdFl",
                        "format": "uri",
                        "maxLength": 65536,
                        "minLength": 1,
                        "type": "string"
                    }
                },
                "required": [
                    "token",
                    "url"
                ],
                "type": "object"
            },
            "AppUsageStatsIn": {
                "properties": {
                    "appIds": {
                        "description": "Specific app IDs or UIDs to aggregate stats for.\n\nNote that if none of the given IDs or UIDs are resolved, a 422 response will be given.",
                        "items": {
                            "description": "The app's ID or UID",
                            "example": "unique-app-identifier",
                            "maxLength": 256,
                            "minLength": 1,
                            "pattern": "^[a-zA-Z0-9\\-_.]+$",
                            "type": "string"
                        },
                        "nullable": true,
                        "type": "array",
                        "uniqueItems": true
                    },
                    "since": {
                        "format": "date-time",
                        "type": "string"
                    },
                    "until": {
                        "format": "date-time",
                        "type": "string"
                    }
                },
                "required": [
                    "since",
                    "until"
                ],
                "type": "object"
            },
            "AppUsageStatsOut": {
                "properties": {
                    "id": {
                        "type": "string"
                    },
                    "status": {
                        "$ref": "#/components/schemas/BackgroundTaskStatus"
                    },
                    "task": {
                        "$ref": "#/components/schemas/BackgroundTaskType"
                    },
                    "unresolvedAppIds": {
                        "description": "Any app IDs or UIDs received in the request that weren't found.\n\nStats will be produced for all the others.",
                        "items": {
                            "description": "The app's ID or UID",
                            "example": "unique-app-identifier",
                            "maxLength": 256,
                            "minLength": 1,
                            "pattern": "^[a-zA-Z0-9\\-_.]+$",
                            "type": "string"
                        },
                        "type": "array",
                        "uniqueItems": true
                    }
                },
                "required": [
                    "id",
                    "status",
                    "task",
                    "unresolvedAppIds"
                ],
                "type": "object"
            },
            "ApplicationIn": {
                "properties": {
                    "metadata": {
                        "additionalProperties": {
                            "type": "string"
                        },
                        "default": {},
                        "type": "object"
                    },
                    "name": {
                        "example": "My first application",
                        "minLength": 1,
                        "type": "string"
                    },
                    "rateLimit": {
                        "format": "uint16",
                        "minimum": 1,
                        "nullable": true,
                        "type": "integer"
                    },
                    "uid": {
                        "description": "Optional unique identifier for the application.",
                        "example": "unique-app-identifier",
                        "maxLength": 256,
                        "minLength": 1,
                        "nullable": true,
                        "pattern": "^[a-zA-Z0-9\\-_.]+$",
                        "type": "string"
                    }
                },
                "required": [
                    "name"
                ],
                "type": "object"
            },
            "ApplicationOut": {
                "properties": {
                    "createdAt": {
                        "format": "date-time",
                        "type": "string"
                    },
                    "id": {
                        "description": "The app's ID",
                        "example": "app_1srOrx2ZWZBpBUvZwXKQmoEYga2",
                        "type": "string"
                    },
                    "metadata": {
                        "additionalProperties": {
                            "type": "string"
                        },
                        "type": "object"
                    },
                    "name": {
                        "example": "My first application",
                        "type": "string"
                    },
                    "rateLimit": {
                        "format": "uint16",
                        "minimum": 0,
                        "nullable": true,
                        "type": "integer"
                    },
                    "uid": {
                        "description": "The app's UID",
                        "example": "unique-app-identifier",
                        "maxLength": 256,
                        "minLength": 1,
                        "nullable": true,
                        "pattern": "^[a-zA-Z0-9\\-_.]+$",
                        "type": "string"
                    },
                    "updatedAt": {
                        "format": "date-time",
                        "type": "string"
                    }
                },
                "required": [
                    "createdAt",
                    "id",
                    "metadata",
                    "name",
                    "updatedAt"
                ],
                "type": "object"
            },
            "ApplicationPatch": {
                "properties": {
                    "metadata": {
                        "additionalProperties": {
                            "type": "string"
                        },
                        "type": "object"
                    },
                    "name": {
                        "type": "string"
                    },
                    "rateLimit": {
                        "format": "uint16",
                        "minimum": 0,
                        "nullable": true,
                        "type": "integer"
                    },
                    "uid": {
                        "description": "The app's UID",
                        "example": "unique-app-identifier",
                        "maxLength": 256,
                        "minLength": 1,
                        "nullable": true,
                        "pattern": "^[a-zA-Z0-9\\-_.]+$",
                        "type": "string"
                    }
                },
                "type": "object"
            },
            "ApplicationStats": {
                "properties": {
                    "appId": {
                        "description": "The app's ID",
                        "example": "app_1srOrx2ZWZBpBUvZwXKQmoEYga2",
                        "type": "string"
                    },
                    "appUid": {
                        "description": "The app's UID",
                        "example": "unique-app-identifier",
                        "maxLength": 256,
                        "minLength": 1,
                        "nullable": true,
                        "pattern": "^[a-zA-Z0-9\\-_.]+$",
                        "type": "string"
                    },
                    "messageDestinations": {
                        "format": "int",
                        "type": "integer"
                    }
                },
                "required": [
                    "appId",
                    "messageDestinations"
                ],
                "type": "object"
            },
            "ApplicationTokenExpireIn": {
                "properties": {
                    "expiry": {
                        "description": "How many seconds until the old key is expired.",
                        "example": 60,
                        "format": "int64",
                        "maximum": 259200,
                        "minimum": 0,
                        "nullable": true,
                        "type": "integer"
                    }
                },
                "type": "object"
            },
            "AttemptStatisticsData": {
                "properties": {
                    "failureCount": {
                        "items": {
                            "format": "int",
                            "type": "integer"
                        },
                        "nullable": true,
                        "type": "array"
                    },
                    "successCount": {
                        "items": {
                            "format": "int",
                            "type": "integer"
                        },
                        "nullable": true,
                        "type": "array"
                    }
                },
                "type": "object"
            },
            "AttemptStatisticsResponse": {
                "properties": {
                    "data": {
                        "$ref": "#/components/schemas/AttemptStatisticsData"
                    },
                    "endDate": {
                        "format": "date-time",
                        "type": "string"
                    },
                    "period": {
                        "$ref": "#/components/schemas/StatisticsPeriod"
                    },
                    "startDate": {
                        "format": "date-time",
                        "type": "string"
                    }
                },
                "required": [
                    "data",
                    "endDate",
                    "period",
                    "startDate"
                ],
                "type": "object"
            },
            "AuthTokenOut": {
                "properties": {
                    "createdAt": {
                        "format": "date-time",
                        "type": "string"
                    },
                    "expiresAt": {
                        "format": "date-time",
                        "nullable": true,
                        "type": "string"
                    },
                    "id": {
                        "description": "The key's ID",
                        "example": "key_1srOrx2ZWZBpBUvZwXKQmoEYga2",
                        "type": "string"
                    },
                    "name": {
                        "nullable": true,
                        "type": "string"
                    },
                    "scopes": {
                        "items": {
                            "type": "string"
                        },
                        "nullable": true,
                        "type": "array"
                    },
                    "token": {
                        "type": "string"
                    }
                },
                "required": [
                    "createdAt",
                    "id",
                    "token"
                ],
                "type": "object"
            },
            "AzureBlobStorageConfig": {
                "properties": {
                    "accessKey": {
                        "type": "string"
                    },
                    "account": {
                        "type": "string"
                    },
                    "container": {
                        "type": "string"
                    }
                },
                "required": [
                    "accessKey",
                    "account",
                    "container"
                ],
                "type": "object"
            },
            "BackgroundTaskData": {
                "type": "object"
            },
            "BackgroundTaskOut": {
                "properties": {
                    "data": {
                        "$ref": "#/components/schemas/BackgroundTaskData"
                    },
                    "id": {
                        "type": "string"
                    },
                    "status": {
                        "$ref": "#/components/schemas/BackgroundTaskStatus"
                    },
                    "task": {
                        "$ref": "#/components/schemas/BackgroundTaskType"
                    }
                },
                "required": [
                    "data",
                    "id",
                    "status",
                    "task"
                ],
                "type": "object"
            },
            "BackgroundTaskStatus": {
                "enum": [
                    "running",
                    "finished",
                    "failed"
                ],
                "type": "string"
            },
            "BackgroundTaskType": {
                "enum": [
                    "endpoint.replay",
                    "endpoint.recover",
                    "application.stats",
                    "message.broadcast",
                    "sdk.generate",
                    "event-type.aggregate"
                ],
                "type": "string"
            },
            "BigQueryConfig": {
                "description": "Configuration for a Google Cloud BigQuery sink.",
                "properties": {
                    "credentials": {
                        "description": "Google Cloud Credentials JSON Object as a string.",
                        "type": "string"
                    },
                    "datasetId": {
                        "type": "string"
                    },
                    "projectId": {
                        "type": "string"
                    },
                    "tableId": {
                        "type": "string"
                    }
                },
                "required": [
                    "credentials",
                    "datasetId",
                    "projectId",
                    "tableId"
                ],
                "type": "object"
            },
            "BorderRadiusConfig": {
                "properties": {
                    "button": {
                        "$ref": "#/components/schemas/BorderRadiusEnum",
                        "nullable": true
                    },
                    "card": {
                        "$ref": "#/components/schemas/BorderRadiusEnum",
                        "nullable": true
                    },
                    "input": {
                        "$ref": "#/components/schemas/BorderRadiusEnum",
                        "nullable": true
                    }
                },
                "type": "object"
            },
            "BorderRadiusEnum": {
                "enum": [
                    "none",
                    "lg",
                    "md",
                    "sm",
                    "full"
                ],
                "type": "string"
            },
            "ClientSecretJwtParamsIn": {
                "properties": {
                    "secretBase64": {
                        "description": "The base64-encoded secret used for signing the JWT.",
                        "type": "string"
                    },
                    "secretId": {
                        "description": "Optional secret identifier. If supplied, this will be populated in the JWT header in the `kid` field.",
                        "nullable": true,
                        "type": "string"
                    },
                    "signingAlgorithm": {
                        "$ref": "#/components/schemas/OauthJwsSigningAlgorithm",
                        "description": "The algorithm used to sign the JWT."
                    },
                    "tokenExpirySecs": {
                        "description": "Optional number of seconds after which the JWT should expire. Defaults to 300 seconds.",
                        "format": "uint64",
                        "minimum": 0,
                        "nullable": true,
                        "type": "integer"
                    }
                },
                "required": [
                    "secretBase64",
                    "signingAlgorithm"
                ],
                "type": "object"
            },
            "CompletionChoice": {
                "properties": {
                    "finish_reason": {
                        "type": "string"
                    },
                    "index": {
                        "format": "int64",
                        "type": "integer"
                    },
                    "message": {
                        "$ref": "#/components/schemas/CompletionMessage"
                    }
                },
                "required": [
                    "finish_reason",
                    "index",
                    "message"
                ],
                "type": "object"
            },
            "CompletionMessage": {
                "properties": {
                    "content": {
                        "type": "string"
                    },
                    "role": {
                        "type": "string"
                    }
                },
                "required": [
                    "content",
                    "role"
                ],
                "type": "object"
            },
            "CountOut": {
                "properties": {
                    "approximated": {
                        "description": "There's a ceiling to how many attempts we count. When the limit is reached, this will be `true` to indicate the actual count is higher than given.",
                        "type": "boolean"
                    },
                    "count": {
                        "description": "The count of attempts matching the query.",
                        "format": "uint64",
                        "minimum": 0,
                        "type": "integer"
                    }
                },
                "required": [
                    "approximated",
                    "count"
                ],
                "type": "object"
            },
            "CreateStreamIn": {
                "properties": {
                    "messages": {
                        "items": {
                            "$ref": "#/components/schemas/EventIn"
                        },
                        "type": "array"
                    },
                    "stream": {
                        "$ref": "#/components/schemas/StreamIn",
                        "description": "Optionally creates a new Stream alongside the events.\n\nIf the stream id or uid that is used in the path already exists, this argument is ignored.",
                        "nullable": true
                    }
                },
                "required": [
                    "messages"
                ],
                "type": "object"
            },
            "CreateStreamOut": {
                "type": "object"
            },
            "CreateTokenIn": {
                "properties": {
                    "expiry": {
                        "description": "How long the token will be valid for, in seconds.",
                        "format": "uint64",
                        "minimum": 0,
                        "nullable": true,
                        "type": "integer"
                    },
                    "name": {
                        "description": "The name of the token.",
                        "type": "string"
                    }
                },
                "required": [
                    "name"
                ],
                "type": "object"
            },
            "CustomColorPalette": {
                "properties": {
                    "backgroundHover": {
                        "format": "color",
                        "nullable": true,
                        "type": "string"
                    },
                    "backgroundPrimary": {
                        "format": "color",
                        "nullable": true,
                        "type": "string"
                    },
                    "backgroundSecondary": {
                        "format": "color",
                        "nullable": true,
                        "type": "string"
                    },
                    "buttonPrimary": {
                        "format": "color",
                        "nullable": true,
                        "type": "string"
                    },
                    "interactiveAccent": {
                        "format": "color",
                        "nullable": true,
                        "type": "string"
                    },
                    "navigationAccent": {
                        "format": "color",
                        "nullable": true,
                        "type": "string"
                    },
                    "primary": {
                        "format": "color",
                        "nullable": true,
                        "type": "string"
                    },
                    "textDanger": {
                        "format": "color",
                        "nullable": true,
                        "type": "string"
                    },
                    "textPrimary": {
                        "format": "color",
                        "nullable": true,
                        "type": "string"
                    }
                },
                "type": "object"
            },
            "CustomStringsOverride": {
                "properties": {
                    "channelsHelp": {
                        "nullable": true,
                        "type": "string"
                    },
                    "channelsMany": {
                        "nullable": true,
                        "type": "string"
                    },
                    "channelsOne": {
                        "nullable": true,
                        "type": "string"
                    }
                },
                "type": "object"
            },
            "CustomThemeOverride": {
                "properties": {
                    "borderRadius": {
                        "$ref": "#/components/schemas/BorderRadiusConfig",
                        "nullable": true
                    },
                    "fontSize": {
                        "$ref": "#/components/schemas/FontSizeConfig",
                        "nullable": true
                    }
                },
                "type": "object"
            },
            "DashboardAccessOut": {
                "properties": {
                    "token": {
                        "example": "appsk_kV3ts5tKPNJN4Dl25cMTfUNdmabxbX0O",
                        "type": "string"
                    },
                    "url": {
                        "example": "https://app.svix.com/login#key=eyJhcHBJZCI6ICJhcHBfMXRSdFl",
                        "format": "uri",
                        "maxLength": 65536,
                        "minLength": 1,
                        "type": "string"
                    }
                },
                "required": [
                    "token",
                    "url"
                ],
                "type": "object"
            },
            "Duration": {
                "properties": {
                    "nanos": {
                        "format": "uint32",
                        "minimum": 0,
                        "type": "integer"
                    },
                    "secs": {
                        "format": "uint64",
                        "minimum": 0,
                        "type": "integer"
                    }
                },
                "required": [
                    "nanos",
                    "secs"
                ],
                "type": "object"
            },
            "EndpointCreatedEvent": {
                "description": "Sent when an endpoint is created.",
                "properties": {
                    "data": {
                        "$ref": "#/components/schemas/EndpointCreatedEventData"
                    },
                    "type": {
                        "default": "endpoint.created",
                        "enum": [
                            "endpoint.created"
                        ],
                        "type": "string"
                    }
                },
                "required": [
                    "data",
                    "type"
                ],
                "type": "object"
            },
            "EndpointCreatedEventData": {
                "description": "Sent when an endpoint is created, updated, or deleted",
                "properties": {
                    "appId": {
                        "description": "The app's ID",
                        "example": "app_1srOrx2ZWZBpBUvZwXKQmoEYga2",
                        "type": "string"
                    },
                    "appUid": {
                        "description": "The app's UID",
                        "example": "unique-app-identifier",
                        "maxLength": 256,
                        "minLength": 1,
                        "nullable": true,
                        "pattern": "^[a-zA-Z0-9\\-_.]+$",
                        "type": "string"
                    },
                    "endpointId": {
                        "description": "The ep's ID",
                        "example": "ep_1srOrx2ZWZBpBUvZwXKQmoEYga2",
                        "type": "string"
                    },
                    "endpointUid": {
                        "description": "The ep's UID",
                        "example": "unique-ep-identifier",
                        "maxLength": 256,
                        "minLength": 1,
                        "nullable": true,
                        "pattern": "^[a-zA-Z0-9\\-_.]+$",
                        "type": "string"
                    }
                },
                "required": [
                    "appId",
                    "endpointId"
                ],
                "type": "object"
            },
            "EndpointDeletedEvent": {
                "description": "Sent when an endpoint is deleted.",
                "properties": {
                    "data": {
                        "$ref": "#/components/schemas/EndpointDeletedEventData"
                    },
                    "type": {
                        "default": "endpoint.deleted",
                        "enum": [
                            "endpoint.deleted"
                        ],
                        "type": "string"
                    }
                },
                "required": [
                    "data",
                    "type"
                ],
                "type": "object"
            },
            "EndpointDeletedEventData": {
                "description": "Sent when an endpoint is created, updated, or deleted",
                "properties": {
                    "appId": {
                        "description": "The app's ID",
                        "example": "app_1srOrx2ZWZBpBUvZwXKQmoEYga2",
                        "type": "string"
                    },
                    "appUid": {
                        "description": "The app's UID",
                        "example": "unique-app-identifier",
                        "maxLength": 256,
                        "minLength": 1,
                        "nullable": true,
                        "pattern": "^[a-zA-Z0-9\\-_.]+$",
                        "type": "string"
                    },
                    "endpointId": {
                        "description": "The ep's ID",
                        "example": "ep_1srOrx2ZWZBpBUvZwXKQmoEYga2",
                        "type": "string"
                    },
                    "endpointUid": {
                        "description": "The ep's UID",
                        "example": "unique-ep-identifier",
                        "maxLength": 256,
                        "minLength": 1,
                        "nullable": true,
                        "pattern": "^[a-zA-Z0-9\\-_.]+$",
                        "type": "string"
                    }
                },
                "required": [
                    "appId",
                    "endpointId"
                ],
                "type": "object"
            },
            "EndpointDisabledEvent": {
                "description": "Sent when an endpoint has been automatically disabled after continuous failures.",
                "properties": {
                    "data": {
                        "$ref": "#/components/schemas/EndpointDisabledEventData"
                    },
                    "type": {
                        "default": "endpoint.disabled",
                        "enum": [
                            "endpoint.disabled"
                        ],
                        "type": "string"
                    }
                },
                "required": [
                    "data",
                    "type"
                ],
                "type": "object"
            },
            "EndpointDisabledEventData": {
                "description": "Sent when an endpoint has been automatically disabled after continuous failures.",
                "properties": {
                    "appId": {
                        "description": "The app's ID",
                        "example": "app_1srOrx2ZWZBpBUvZwXKQmoEYga2",
                        "type": "string"
                    },
                    "appUid": {
                        "description": "The app's UID",
                        "example": "unique-app-identifier",
                        "maxLength": 256,
                        "minLength": 1,
                        "nullable": true,
                        "pattern": "^[a-zA-Z0-9\\-_.]+$",
                        "type": "string"
                    },
                    "endpointId": {
                        "description": "The ep's ID",
                        "example": "ep_1srOrx2ZWZBpBUvZwXKQmoEYga2",
                        "type": "string"
                    },
                    "endpointUid": {
                        "description": "The ep's UID",
                        "example": "unique-ep-identifier",
                        "maxLength": 256,
                        "minLength": 1,
                        "nullable": true,
                        "pattern": "^[a-zA-Z0-9\\-_.]+$",
                        "type": "string"
                    },
                    "failSince": {
                        "format": "date-time",
                        "type": "string"
                    }
                },
                "required": [
                    "appId",
                    "endpointId",
                    "failSince"
                ],
                "type": "object"
