[package]
name = "asciinema"
version = "3.0.0-rc.3"
edition = "2021"
authors = ["Marcin Kulik <m@ku1ik.com>"]
homepage = "https://asciinema.org"
repository = "https://github.com/asciinema/asciinema"
description = "Terminal session recorder"
license = "GPL-3.0"

# MSRV
rust-version = "1.70.0"

# See more keys and their definitions at https://doc.rust-lang.org/cargo/reference/manifest.html

[dependencies]
anyhow = "1.0.75"
nix = { version = "0.27", features = [ "fs", "term", "process", "signal" ] }
termion = "3.0.0"
serde = { version = "1.0.189", features = ["derive"] }
serde_json = "1.0.107"
clap = { version = "4.4.7", features = ["derive"] }
signal-hook = { version = "0.3.17", default-features = false }
uuid = { version = "1.6.1", features = ["v4"] }
reqwest = { version = "0.12.8", default-features = false, features = ["blocking", "rustls-tls-native-roots", "multipart", "gzip", "json"] }
rustyline = "13.0.0"
config = { version = "0.14.0", default-features = false, features = ["toml", "ini"] }
which = "6.0.0"
tempfile = "3.9.0"
scraper = { version = "0.19.0", default-features = false }
avt = "0.14.0"
axum = { version = "0.7.7", default-features = false, features = ["http1", "ws"] }
tokio = { version = "1.35.1", features = ["full"] }
futures-util = "0.3.30"
tokio-stream = { version = "0.1.14", features = ["sync"] }
rust-embed = "8.2.0"
mime_guess = "2.0.4"
tower-http = { version = "0.5.1", features = ["trace"] }
tracing = "0.1.40"
tracing-subscriber = { version = "0.3.18", features = ["env-filter"] }
rgb = "0.8.37"
url = "2.5.0"
tokio-tungstenite = { version = "0.24.0", features = ["rustls-tls-native-roots"] }
rustls = { version = "0.23.15", default-features = false, features = ["aws_lc_rs"] }
sha2 = "0.10.8"
tokio-util = "0.7.10"
chrono = "0.4.38"
hostname = "0.4.0"

[build-dependencies]
clap = { version = "4.4.7", features = ["derive"] }
clap_complete = "4.4.10"
clap_mangen = "0.2.19"
url = "2.5.0"

[profile.release]
strip = true

use clap::CommandFactory;
use clap::ValueEnum;
use std::env;
use std::fs::create_dir_all;
use std::path::Path;
use std::path::PathBuf;

mod cli {
    include!("src/cli.rs");
}

const ENV_KEY: &str = "ASCIINEMA_GEN_DIR";

fn main() -> std::io::Result<()> {
    if let Some(dir) = env::var_os(ENV_KEY).or(env::var_os("OUT_DIR")) {
        let mut cmd = cli::Cli::command();
        let base_dir = PathBuf::from(dir);

        let man_dir = Path::join(&base_dir, "man");
        create_dir_all(&man_dir)?;
        clap_mangen::generate_to(cmd.clone(), &man_dir)?;

        let completion_dir = Path::join(&base_dir, "completion");
        create_dir_all(&completion_dir)?;

        for shell in clap_complete::Shell::value_variants() {
            clap_complete::generate_to(*shell, &mut cmd, "asciinema", &completion_dir)?;
        }
    }

    println!("cargo:rustc-env=TARGET={}", env::var("TARGET").unwrap());
    println!("cargo:rerun-if-env-changed={ENV_KEY}");

    Ok(())
}

{
  lib,
  stdenv,
  rust,
  makeRustPlatform,
  packageToml,
  libiconv,
  darwin,
  python3,
}:
(makeRustPlatform {
  cargo = rust;
  rustc = rust;
})
.buildRustPackage {
  pname = packageToml.name;
  inherit (packageToml) version;

  src = builtins.path {
    path = ./.;
    inherit (packageToml) name;
  };

  dontUseCargoParallelTests = true;

  cargoLock.lockFile = ./Cargo.lock;

  nativeBuildInputs = [rust];
  buildInputs = lib.optional stdenv.isDarwin [
    libiconv
    darwin.apple_sdk.frameworks.Foundation
  ];

  nativeCheckInputs = [python3];
}

{
  description = "Terminal session recorder";

  inputs = {
    nixpkgs.url = github:NixOS/nixpkgs/nixos-unstable;
    rust-overlay.url = github:oxalica/rust-overlay;
    flake-parts.url = github:hercules-ci/flake-parts;
  };

  outputs = inputs @ {
    flake-parts,
    rust-overlay,
    ...
  }:
    flake-parts.lib.mkFlake {inherit inputs;} {
      systems = ["x86_64-linux" "aarch64-linux" "aarch64-darwin" "x86_64-darwin"];
      perSystem = {
        self',
        pkgs,
        system,
        ...
      }: let
        packageToml = (builtins.fromTOML (builtins.readFile ./Cargo.toml)).package;
      in {
        formatter = pkgs.alejandra;

        _module.args = {
          pkgs = import inputs.nixpkgs {
            inherit system;
            overlays = [(import rust-overlay)];
          };
        };

        devShells = pkgs.callPackages ./shell.nix {inherit packageToml self';};

        packages.default = pkgs.callPackage ./default.nix {
          inherit packageToml;
          rust = pkgs.rust-bin.stable.latest.minimal;
        };
      };
    };
}

{
  self',
  packageToml,
  rust-bin,
  mkShell,
}:
let
  msrv = packageToml.rust-version;

  mkDevShell =
    rust:
    mkShell {
      inputsFrom = [
        (self'.packages.default.override {
          rust = rust.override {
            extensions = [
              "rust-src"
              "rust-analyzer"
            ];
          };
        })
      ];

      env.RUST_BACKTRACE = 1;
    };
in
{
  default = mkDevShell rust-bin.stable.latest.default;
  msrv = mkDevShell rust-bin.stable.${msrv}.default;
}
